Question.create({"id"=>68149, "text"=>"What types of data, metadata, and scripts will you collect, create, link to, acquire, record, or generate through the proposed research project?", "default_value"=>nil, "number"=>1, "section_id"=>22128, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"7f924875-2ec4-446f-ae09-fb22e84fe6ec"})
Annotation.create({"id"=>63900, "question_id"=>68149, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Examples of data types may include text, numeric (ASCII, binary), images, audio, video, tabular data, spatial data, experimental, observational, and simulation/modelling data, instrumentation data, codes, software and algorithms, and any other materials that may be produced in the course of the project.</span>", "type"=>"guidance", "versionable_id"=>"f45abfc0-3ed4-4e31-a50c-b34471711131"})
Section.create({"id"=>22129, "title"=>"Data Organization", "description"=>nil, "number"=>2, "phase_id"=>3440, "modifiable"=>true, "versionable_id"=>"56b7719e-3212-453b-a239-f93d4f87f719"})
Question.create({"id"=>68150, "text"=>"In what file formats will your data be collected and generated? Will these formats allow for data re-use, sharing and long-term access to the data?", "default_value"=>nil, "number"=>1, "section_id"=>22129, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"0b6a1acd-9b59-4be9-bd67-21ef3ed75b20"})
Annotation.create({"id"=>63901, "question_id"=>68150, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Proprietary file formats that require specialized software or hardware are not recommended, but may be necessary for certain data collection or analysis methods. Using open file formats or industry-standard formats (e.g. those widely used by a given community) is preferred whenever possible. Read more about recommended file formats at </span><a href=\"https://researchdata.library.ubc.ca/plan/format-your-data/\"><span style=\"font-weight: 400;\">UBC Library</span></a><span style=\"font-weight: 400;\"> or </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/format.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"8a82b957-87bd-4578-a455-c21ca626c034"})
Question.create({"id"=>68151, "text"=>"What conventions and procedures will you use to structure, name and version-control your files to help you and others better understand how your data are organized?", "default_value"=>nil, "number"=>2, "section_id"=>22129, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"0f7a0bbf-837e-4928-8a0f-0ac53046f702"})
Annotation.create({"id"=>63902, "question_id"=>68151, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">It is important to keep track of different copies and versions of files, files held in different formats or locations, and any information cross-referenced between files.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">Logical file structures, informative naming conventions, and clear indications of file versions all contribute to better use of your data during and after your research project. These practices will help ensure that you and your research team are using the appropriate version of your data, and will minimize confusion regarding copies on different computers, on different media, in different formats, and/or in different locations. Read more about file naming and version control at </span><a href=\"https://researchdata.library.ubc.ca/plan/organize-your-data/\"><span style=\"font-weight: 400;\">UBC Library</span></a><span style=\"font-weight: 400;\"> or </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/format/organising.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"07440541-24d0-44fb-8290-6a1463f090bc"})
Phase.create({"id"=>3441, "title"=>"Phase 2: Active Research (Data) Management", "description"=>nil, "number"=>2, "template_id"=>3465, "modifiable"=>true, "versionable_id"=>"4b54c857-03ae-486b-884b-1c90a2b4e4bc"})
Section.create({"id"=>22130, "title"=>"Documentation and Metadata", "description"=>nil, "number"=>1, "phase_id"=>3441, "modifiable"=>true, "versionable_id"=>"908fa18f-c99e-4fac-b256-06cfa0f2affe"})
Question.create({"id"=>68152, "text"=>"What information will be needed for the data to be read and interpreted correctly?", "default_value"=>nil, "number"=>1, "section_id"=>22130, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"c5e1f309-2af4-4d6e-8fb1-3391580cc6cc"})
Annotation.create({"id"=>63903, "question_id"=>68152, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Some types of documentation typically provided for research data and software include:&nbsp;</span></p>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">README file, codebook, or data dictionary</span></li>\r\n<li><span style=\"font-weight: 400;\">Electronic lab notebooks such as </span><a href=\"https://jupyter.org/\"><span style=\"font-weight: 400;\">Jupyter Notebook</span></a></li>\r\n</ul>", "type"=>"example_answer", "versionable_id"=>"b0466155-dfc1-4eba-b2b6-0e8292404c50"})
Annotation.create({"id"=>63904, "question_id"=>68152, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Typically, good documentation includes high-level information about the study as well as data-level descriptions of the content. It may also include other contextual information required to make the data usable by other researchers, such as: your research methodology, definitions of variables, vocabularies, classification systems, units of measurement, assumptions made, formats and file types of the data, a description of the data capture and collection methods, provenance of various data sources (original source of data, and how the data have been transformed), explanation of data analysis performed (including syntax files), the associated script(s), and annotation of relevant software. </span>", "type"=>"guidance", "versionable_id"=>"69f57e05-3a55-4cf4-8ece-d6776be76a2b"})
Question.create({"id"=>68153, "text"=>"If you are using a metadata standard and/or tools to document and describe your data, please list them here. Explain the rationale for the selection of these standards.", "default_value"=>nil, "number"=>2, "section_id"=>22130, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"26198ef9-51d1-4997-a544-0eee6772d87c"})
Annotation.create({"id"=>63905, "question_id"=>68153, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">There are many general and domain-specific metadata standards that can be used to manage research data. These machine-readable, openly-accessible standards are often based on language-independent data formats such as XML, RDF, and JSON, which enables the effective exchange of information between users and systems. Existing, accepted community standards should be used wherever possible, including when recording intermediate results.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Where community standards are absent or inadequate, this should be documented along with any proposed solutions or remedies. You may wish to use this DMP Template to propose alternate strategies that will facilitate metadata interoperability in your field. </span></p>", "type"=>"guidance", "versionable_id"=>"c78b846a-d81f-46cf-af87-3904970db920"})
Annotation.create({"id"=>63906, "question_id"=>68153, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">There are a wide variety of metadata standards available to choose from, and you can learn more about these options at </span><a href=\"http://www.dcc.ac.uk/resources/metadata-standards\"><span style=\"font-weight: 400;\">UK Digital Curation Centre's Disciplinary Metadata</span></a><span style=\"font-weight: 400;\">, </span><a href=\"https://fairsharing.org/standards/\"><span style=\"font-weight: 400;\">FAIRsharing standards</span></a><span style=\"font-weight: 400;\">, </span><a href=\"http://rd-alliance.github.io/metadata-directory/\"><span style=\"font-weight: 400;\">RDA Metadata Standards Directory</span></a><span style=\"font-weight: 400;\">, </span><a href=\"http://jennriley.com/metadatamap/\"><span style=\"font-weight: 400;\">Seeing Standards: A Visualization of the Metadata Universe</span></a><span style=\"font-weight: 400;\">.</span></p>", "type"=>"example_answer", "versionable_id"=>"536f9938-a2ba-46ef-bfb8-89bff810fc19"})
Question.create({"id"=>68154, "text"=>"How will you make sure that documentation is created or captured consistently throughout your project?", "default_value"=>nil, "number"=>3, "section_id"=>22130, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"26ec06e9-b953-4c25-a70e-b5c52acb9b31"})
Annotation.create({"id"=>63907, "question_id"=>68154, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Consider how you will capture information during the project and where it will be recorded to ensure the accuracy, consistency, and completeness of your documentation. Often, resources you've already created can contribute to this (e.g. publications, websites, progress reports, etc.). It is useful to consult regularly with members of the research team to capture potential changes in data collection or processing that need to be reflected in the documentation. Individual roles and workflows should include gathering, creating or maintaining data documentation as a key element.</span>", "type"=>"guidance", "versionable_id"=>"0fbb9f62-e31f-4e66-b12d-230ccbdb250a"})
Section.create({"id"=>22131, "title"=>"Advanced Research Computing (ARC)-Related Facilities and Other Resources", "description"=>nil, "number"=>2, "phase_id"=>3441, "modifiable"=>true, "versionable_id"=>"0c468ef8-3821-4f67-9342-13cc0cd70009"})
Question.create({"id"=>68155, "text"=>"Please identify the facilities to be used (laboratory, computer, office, clinical and other) and/or list the organizational resources available to perform the proposed research. If appropriate, indicate the capacity, pertinent capabilities, relative proximity and extent of availability of the resources to the research project.", "default_value"=>nil, "number"=>1, "section_id"=>22131, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"258a0679-f9b8-445a-9c18-d4d6887e5b03"})
Annotation.create({"id"=>63908, "question_id"=>68155, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">ARC resources usually contain both computational resources and data storage resources. Please describe only those ARC resources that are directly applicable to the proposed work. Include existing resources and any external resources that may be made available.</span>", "type"=>"guidance", "versionable_id"=>"bdd3982c-08b8-4828-a627-27409724ba55"})
