Annotation.create({"id"=>63909, "question_id"=>68157, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">You may wish to provide the following information:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Startup allocation limit</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">System architecture: System component and configuration</span></li>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">CPU nodes</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">GPU nodes</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Large memory nodes</span></li>\r\n</ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Performance: e.g., FLOPs, benchmark</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Associated systems software environment</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Supported application software&nbsp;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data transfer</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Storage </span></li>\r\n</ul>", "type"=>"example_answer", "versionable_id"=>"d1d84573-be40-4ee8-9b4b-db3fc72cf1e2"})
Annotation.create({"id"=>63910, "question_id"=>68157, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">It is important to document the</span> <span style=\"font-weight: 400;\">technical details of all the computational and data storage resources, and</span> <span style=\"font-weight: 400;\">associated systems and software environments you plan to use to perform the simulations and analysis proposed in this research project. </span>", "type"=>"guidance", "versionable_id"=>"e1d0b69e-1bc0-4252-a16b-278a3c9508fb"})
