Section.create({"id"=>22177, "title"=>"Data Production", "description"=>nil, "number"=>1, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"30ab6347-9286-4236-b292-c12f3471765a"})
Question.create({"id"=>68298, "text"=>"What type(s) of data will be produced and in what format(s)?", "default_value"=>nil, "number"=>1, "section_id"=>22177, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"ab67b7f4-714b-4da2-af3c-60411169fa9f"})
Annotation.create({"id"=>64040, "question_id"=>68298, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Examples of experimental image data may include: Magnetic Resonance (MR), X-ray, Fluorescent Resonance Energy Transfer (FRET), Fluorescent Lifetime Imaging (FLIM), Atomic-Force Microscopy (AFM), Electron Paramagnetic Resonance (EPR), Laser Scanning Microscope (LSM), Extended X-ray Absorption Fine Structure (EXAFS), Femtosecond X-ray, Raman spectroscopy, or other digital imaging methods. </span>", "type"=>"example_answer", "versionable_id"=>"80386c7d-6d15-4c8c-b70a-9782970c3820"})
Annotation.create({"id"=>64041, "question_id"=>68298, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Describe the type(s) of data that will be collected, such as: text, numeric (ASCII, binary), images, and animations.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">List the file formats you expect to use. Keep in mind that some file formats are optimal for the long-term preservation of data, for example, non-proprietary formats such as text ('.txt'), comma-separated ('.csv'), TIFF (&lsquo;.tiff&rsquo;), JPEG (&lsquo;.jpg&rsquo;), and MPEG-4 (&lsquo;.m4v&rsquo;, &lsquo;mp4&rsquo;). Files converted from one format to another for archiving and preservation may lose information (e.g. converting from an uncompressed TIFF file to a compressed JPG file), so changes to file formats should be documented. Guidance on file formats recommended for sharing and preservation are available from the </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/format/recommended-formats.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">, </span><a href=\"https://guides.library.cornell.edu/ecommons/formats\"><span style=\"font-weight: 400;\">Cornell&rsquo;s Digital Repository</span></a><span style=\"font-weight: 400;\">, the </span><a href=\"https://www.ed.ac.uk/information-services/research-support/research-data-service/after/data-repository/choosing-file-formats\"><span style=\"font-weight: 400;\">University of Edinburgh</span></a><span style=\"font-weight: 400;\">, and the </span><a href=\"https://researchdata.library.ubc.ca/plan/format-your-data/\"><span style=\"font-weight: 400;\">University of British Columbia</span></a><span style=\"font-weight: 400;\">. </span>", "type"=>"guidance", "versionable_id"=>"4c249ccd-12cf-4049-b06c-207bb7892b50"})
Question.create({"id"=>68299, "text"=>"Does this project involve the use or analysis of secondary data? What is the data-sharing arrangement for these data?", "default_value"=>nil, "number"=>2, "section_id"=>22177, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"3c4a84ea-35ab-4e3c-9481-6658e44c47b8"})
Annotation.create({"id"=>64042, "question_id"=>68299, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Describe any secondary data you expect to reuse. List any available documentation, licenses, or terms of use assigned. If the data have a DOI or unique accession number, include that information and the name of the repository or database that holds the data. This will allow you to easily navigate back to the data, and properly cite and acknowledge the data creators in any publication or research output.&nbsp;&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">For data that are not publicly available, you may have entered into a data-sharing arrangement with the data owner, which should be noted here.&nbsp; A data-sharing arrangement describes what data are being shared and how the data can be used. It can be a license agreement or any other formal agreement, for example, an agreement to use copyright-protected data. </span></p>", "type"=>"guidance", "versionable_id"=>"dcc9b366-e2cb-4c4a-bca1-1f04b0cd0d9f"})
Annotation.create({"id"=>64043, "question_id"=>68299, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">An example of experimental data from a secondary data source may be structures or parameters obtained from </span><a href=\"https://www.rcsb.org/pages/about-us/index\"><span style=\"font-weight: 400;\">Protein Data Bank</span></a><span style=\"font-weight: 400;\"> (PDB).&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Examples of computational data and data sources may include: log files, parameters, structures, or trajectory files from previous modeling/simulations or tests. </span></p>", "type"=>"example_answer", "versionable_id"=>"fd6a7d30-85b2-4b7f-ba9a-96866b5721bb"})
Question.create({"id"=>68300, "text"=>"What tools, devices, platforms, and/or software packages will be used to generate and manipulate data during the project?", "default_value"=>nil, "number"=>3, "section_id"=>22177, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"7f80ec41-591c-4737-8408-1a97ce128e49"})
Annotation.create({"id"=>64044, "question_id"=>68300, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">List all devices and/or instruments and describe the experimental setup (if applicable) that will be utilized to collect empirical data. For commercial instruments or devices, indicate the brand, type, and other necessary characteristics for identification. For a home-built experimental setup, list the components, and describe the functional connectivity. Use diagrams when essential for clarity.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Indicate the program and software package with the version you will use to prepare a structure or a parameter file for modeling or simulation. If web-based services such as </span><a href=\"http://protein.ict.ac.cn/FALCON/\"><span style=\"font-weight: 400;\">FALCON@home</span></a><span style=\"font-weight: 400;\">, </span><a href=\"https://www.vlifesciences.com/products/VLifeMDS/Protein_Modeller.php\"><span style=\"font-weight: 400;\">ProModel</span></a><span style=\"font-weight: 400;\">, </span><a href=\"http://www.charmm-gui.org/\"><span style=\"font-weight: 400;\">CHARMM-GUI</span></a><span style=\"font-weight: 400;\">, etc. will be used to prepare or generate data, provide details such as the name of the service, URL, version (if applicable), and description of how you plan to use it.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">If you plan to use your own software or code, specify where and how it can be obtained to independently verify computational outputs and reproduce figures by providing the DOI, link to GitHub, or another source. Specify if research collaboration platforms such as </span><a href=\"https://codeocean.com/\"><span style=\"font-weight: 400;\">CodeOcean</span></a><span style=\"font-weight: 400;\">, or </span><a href=\"https://wholetale.org/\"><span style=\"font-weight: 400;\">WholeTale</span></a><span style=\"font-weight: 400;\"> will be used to create, execute, and publish computational findings. </span>", "type"=>"guidance", "versionable_id"=>"f084e269-7304-4fac-b32b-ac62b0acf88e"})
Question.create({"id"=>68301, "text"=>"Describe the data flow through the entire project. What steps will you take to increase the likelihood that your results will be reproducible?", "default_value"=>nil, "number"=>4, "section_id"=>22177, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"37dd29f3-612e-466f-80d6-4a386d2cf598"})
Annotation.create({"id"=>64045, "question_id"=>68301, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Reproducible computational practices are critical to continuing progress within the discipline. At a high level, describe the steps you will take to ensure computational reproducibility in your project, including the operations you expect to perform on the data, and the path taken by the data through systems, devices, and/or procedures. Describe how an instrument response function will be obtained, and operations and/or procedures through which data (research and computational outputs) can be replicated. Indicate what documentation will be provided to ensure the reproducibility of your research and computational outputs.</span>", "type"=>"guidance", "versionable_id"=>"f688b884-f96e-4757-ab52-c5af219f3bf5"})
Annotation.create({"id"=>64046, "question_id"=>68301, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Some examples of data procedures include: data normalization, data fitting, data convolution, or Fourier transformation.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Examples of documentation may include: syntax, code, algorithm(s), parameters, device log files, or manuals.&nbsp; </span></p>", "type"=>"example_answer", "versionable_id"=>"994ee138-55e1-4404-82b3-57e2878e029c"})
Section.create({"id"=>22178, "title"=>"Documentation and Metadata", "description"=>nil, "number"=>2, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"04d3a032-fbc9-440d-89e5-5bf25780efac"})
Question.create({"id"=>68302, "text"=>"What documentation will be needed for the data to be read and interpreted correctly in the future?", "default_value"=>nil, "number"=>1, "section_id"=>22178, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"f0604451-12b1-47f9-8e12-c73cd389af5e"})
Annotation.create({"id"=>64047, "question_id"=>68302, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Documentation can be provided in the form of a README file with information to ensure the data can be correctly interpreted by you or by others when you share or publish your data. Typically, a README file includes a description (or abstract) of the study, and any other contextual information required to make data usable by other researchers. Other information could include: research methodology used, variable definitions, vocabularies, units of measurement, assumptions made, format and file type of the data, a description of the data origin and production, an explanation of data file formats that were converted, a description of data analysis performed, a description of the computational environment, references to external data sources, details of who worked on the project and performed each task. Further guidance on writing README files is available from the University of British Columbia (</span><a href=\"https://researchdata-06oct2014.sites.olt.ubc.ca/files/2020/04/QuickGuide_UBC_readme_v1.0_20200427.pdf\"><span style=\"font-weight: 400;\">UBC</span></a><span style=\"font-weight: 400;\">) and </span><a href=\"https://data.research.cornell.edu/content/readme\"><span style=\"font-weight: 400;\">Cornell</span></a><span style=\"font-weight: 400;\"> University.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Consider also saving detailed information during the course of the project in a log file. A log file will help to manage and resolve issues that arise during a project and prioritize the response to them. It may include events that occur in an operating system, messages between users, run time and error messages for troubleshooting. The level of detail in a log file depends on the complexity of your project. </span></p>", "type"=>"guidance", "versionable_id"=>"a961d0b7-d516-4014-b774-8174d3336c95"})
Question.create({"id"=>68303, "text"=>"How will you make sure that documentation is created or captured consistently throughout your project? What approaches will be employed by the research team to access, modify, and contribute data throughout the project?", "default_value"=>nil, "number"=>2, "section_id"=>22178, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"a5d6a7d4-9ebd-441c-abc3-9d3fb01babd7"})
Annotation.create({"id"=>64048, "question_id"=>68303, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Consider how you will capture this information in advance of data production and analysis, to ensure accuracy, consistency, and completeness of the documentation.&nbsp; Often, resources you've already created can contribute to this (e.g. publications, websites, progress reports, etc.).&nbsp; It is useful to consult regularly with members of the research team to capture potential changes in data collection/processing that need to be reflected in the documentation. Individual roles and workflows should include gathering data documentation as a key element.</span></p>\r\n<span style=\"font-weight: 400;\">Describe how data will be shared and accessed by the members of the research group during the project. Provide details of the data management service or tool (name, URL) that will be utilized to share data with the research group (e.g. Open Science Framework [</span><a href=\"https://osf.io/\"><span style=\"font-weight: 400;\">OSF]</span></a><span style=\"font-weight: 400;\">&nbsp;or </span><a href=\"https://www.radiam.ca/\"><span style=\"font-weight: 400;\">Radiam</span></a><span style=\"font-weight: 400;\">).</span>", "type"=>"guidance", "versionable_id"=>"8c3497f9-7a59-4df4-acf3-3dead8eeaac3"})
Question.create({"id"=>68304, "text"=>"<p>If you are using a metadata standard and/or online tools to document and describe your data, please list them here.</p>", "default_value"=>nil, "number"=>3, "section_id"=>22178, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"705e04e3-8143-4627-a71e-ca8b163ce2d5"})
Annotation.create({"id"=>64049, "question_id"=>68304, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">There are many general and domain-specific metadata standards. Dataset documentation should be provided in a standard, machine-readable, openly-accessible format to enable the effective exchange of information between users and systems. These standards are often based on language-independent data formats such as XML, RDF, and JSON. Read more about metadata standards here: </span><a href=\"http://dcc.ac.uk/resources/metadata-standards\"><span style=\"font-weight: 400;\">UK Digital Curation Centre's Disciplinary Metadata</span></a><span style=\"font-weight: 400;\">. </span></p>", "type"=>"guidance", "versionable_id"=>"9214a907-2d2b-4529-82e0-6b4430bd1ff8"})
Section.create({"id"=>22179, "title"=>"Storage and Backup", "description"=>nil, "number"=>3, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"756c8000-9858-4708-a8a1-87bef623f457"})
Question.create({"id"=>68305, "text"=>"What are the anticipated storage requirements for your project, in terms of storage space (in megabytes, gigabytes, terabytes, etc.) and the length of time you will be storing it?", "default_value"=>nil, "number"=>1, "section_id"=>22179, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"6f893e83-b902-4936-91a1-54ac849a0278"})
Annotation.create({"id"=>64050, "question_id"=>68305, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Storage-space estimates should consider requirements for file versioning, backups, and growth over time.&nbsp; If you are collecting data over a long period (e.g. several months or years), your data storage and backup strategy should accommodate data growth. Similarly, a long-term storage plan is necessary if you intend to retain your data after the research project. </span>", "type"=>"guidance", "versionable_id"=>"9a9335a3-8b84-486f-b40e-1ff7c00acee5"})
Question.create({"id"=>68306, "text"=>"How and where will your data be stored and backed up during your research project?", "default_value"=>nil, "number"=>2, "section_id"=>22179, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"5b99e052-4eb8-4388-8ec2-a2937c6f88f2"})
Annotation.create({"id"=>64051, "question_id"=>68306, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Data may be stored using optical or magnetic media, which can be removable (e.g. DVD and USB drives), fixed (e.g. desktop or laptop hard drives), or networked (e.g. networked drives or cloud-based servers). Each storage method has benefits and drawbacks that should be considered when determining the most appropriate solution.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">The risk of losing data due to human error, natural disasters, or other mishaps can be mitigated by following the </span><a href=\"http://dataabinitio.com/?p=501\"><span style=\"font-weight: 400;\">3-2-1 backup rule</span></a>:\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">Have at least three copies of your data.<br /></span></li>\r\n<li><span style=\"font-weight: 400;\">Store the copies on two different media.</span></li>\r\n<li><span style=\"font-weight: 400;\">Keep one backup copy offsite.</span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"fa83b113-8821-42d7-bd6f-8e31496046f6"})
Section.create({"id"=>22180, "title"=>"Sharing and Archiving", "description"=>nil, "number"=>4, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"845adc68-1a26-4f68-83f7-87ac28b43d06"})
Question.create({"id"=>68307, "text"=>"What data will you be sharing and in what form?", "default_value"=>nil, "number"=>1, "section_id"=>22180, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"9037505d-00ae-425c-b43b-3e5a9932beaf"})
Annotation.create({"id"=>64052, "question_id"=>68307, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Consider which data need to be shared to meet institutional, funding, or industry requirements. Consider which data will need to be restricted because of data-sharing arrangements with third parties, or other intellectual property considerations.&nbsp;<br /></span></p>\r\n<p><span style=\"font-weight: 400;\">In this context, data might include research and computational findings, software, code, algorithms, or any other outputs (research or computational) that may be generated during the project. Research outputs might be in the form of:<br /></span></p>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\"><strong>raw data</strong> are the data directly obtained from the simulation or modeling;</span></li>\r\n<li><span style=\"font-weight: 400;\"><strong>processed data</strong> result from some manipulation of the raw data in order to eliminate errors or outliers, to prepare the data for analysis, or to derive new variables; or </span></li>\r\n<li><span style=\"font-weight: 400;\"><strong>analyzed data</strong> are the results of quantitative, statistical, or mathematical analysis of the processed data. </span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"ed4acabe-10df-49d9-b014-45732c3c6d5e"})
Question.create({"id"=>68308, "text"=>"What steps will be taken to help the research community know that your research data exist?", "default_value"=>nil, "number"=>2, "section_id"=>22180, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"aebf9124-cdea-43e8-b0f4-4ea87618c728"})
Annotation.create({"id"=>64053, "question_id"=>68308, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Possibilities include: data registries, data repositories, indexes, word-of-mouth, and publications. If possible, choose to archive your data in a repository that will assign a persistent identifier (such as a DOI) to your dataset. This will ensure stable access to the dataset and make it retrievable by various discovery tools.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">One of the best ways to refer other researchers to your deposited datasets is to cite them the same way you cite other types of publications (articles, books, proceedings). The Digital Curation Centre provides a detailed </span><a href=\"http://www.dcc.ac.uk/resources/how-guides/cite-datasets\"><span style=\"font-weight: 400;\">guide</span></a><span style=\"font-weight: 400;\"> on data citation. Note that some data repositories also create links from datasets to their associated papers, thus increasing the visibility of the publications.&nbsp;</span><strong>&nbsp;</strong>", "type"=>"guidance", "versionable_id"=>"7bb7b378-5516-472f-bf7e-0b582154dd96"})
Question.create({"id"=>68309, "text"=>"If you will use your own code or software in this project, describe your strategies for sharing it with other researchers.", "default_value"=>nil, "number"=>3, "section_id"=>22180, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"03939b11-2489-45ee-9f9f-cdc6c7b039bf"})
Annotation.create({"id"=>64054, "question_id"=>68309, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Some strategies for sharing include:<br /></span></p>\r\n<ol>\r\n<li><span style=\"font-weight: 400;\">Research collaboration platforms such as <a href=\"https://codeocean.com/\">Code Ocean</a>, <a href=\"https://wholetale.org/\">Whole Tale,</a> or other, will be used to create, execute, share, and publish computational code and data;</span></li>\r\n<li><span style=\"font-weight: 400;\"><a href=\"https://github.com/\">GitHub</a>, <a href=\"https://about.gitlab.com/\">GitLab</a> or <a href=\"https://bitbucket.org/product?&amp;aceid=&amp;adposition=&amp;adgroup=92542387895&amp;campaign=9128560521&amp;creative=414608948607&amp;device=c&amp;keyword=bitbucket&amp;matchtype=e&amp;network=g&amp;placement=&amp;ds_kids=p51241296780&amp;ds_e=GOOGLE&amp;ds_eid=700000001551985&amp;ds_e1=GOOGLE&amp;gclid=EAIaIQobChMI9qOg0P_-6QIV2uKGCh2tmQDZEAAYASAAEgKJyfD_BwE&amp;gclsrc=aw.ds\">Bitbucket</a> will be utilized to allow for version control; </span></li>\r\n<li><span style=\"font-weight: 400;\">A general public license (e.g., <a href=\"https://www.gnu.org/licenses/gpl-3.0.en.html\">GNU/GPL</a> or <a href=\"https://opensource.org/licenses/MIT\">MIT</a>) will be applied to allow others to run, study, share, and modify the code or software. For further information about licenses see, for example, the <a href=\"https://opensource.org/licenses\">Open Source Initiative</a>; </span></li>\r\n<li><span style=\"font-weight: 400;\">The code or software will be archived in a repository, and a DOI will be assigned to track use through citations. Provide the name of the repository;</span></li>\r\n<li><span style=\"font-weight: 400;\">A software patent will be submitted. </span></li>\r\n</ol>", "type"=>"guidance", "versionable_id"=>"2de5ee93-1ff5-47db-a980-4ebaf2f503ea"})
Question.create({"id"=>68310, "text"=>"<p>Which data (research and computational outputs) will be retained after the completion of the project? Where will your research data be archived for the long-term? Describe your strategies for long-term data archiving.</p>", "default_value"=>nil, "number"=>4, "section_id"=>22180, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"be3e6cf4-6936-402c-b78a-7331217c329a"})
Annotation.create({"id"=>64055, "question_id"=>68310, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">In cases where only selected data will be retained, indicate the reason(s) for this decision. These might include legal, physical preservation issues or other requirements to keep or destroy data.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">There are general-purpose data repositories available in Canada, such as </span><a href=\"https://dataverse.scholarsportal.info/\"><span style=\"font-weight: 400;\">Scholars Portal Dataverse</span></a><span style=\"font-weight: 400;\">, and the Federated Research Data Repository (</span><a href=\"https://www.frdr-dfdr.ca/repo/\"><span style=\"font-weight: 400;\">FRDR</span></a><span style=\"font-weight: 400;\">), which have preservation policies in place. Many research disciplines also have dedicated repositories. </span><a href=\"https://www.nature.com/sdata/policies/repositories#general\"><span style=\"font-weight: 400;\">Scientific Data</span></a><span style=\"font-weight: 400;\">, for example, provides a list of repositories for scientific research outputs.&nbsp; Some of these repositories may request or require data to be submitted in a specific file format. You may wish to consult with a repository early in your project, to ensure your data are generated in, or can be transformed into, an appropriate standard format for your field. </span><a href=\"https://www.re3data.org/\"><span style=\"font-weight: 400;\">Re3data.org</span></a><span style=\"font-weight: 400;\"> is an international registry of research data repositories that may help you identify a suitable repository for your data. The </span><a href=\"https://repositoryfinder.datacite.org/\"><span style=\"font-weight: 400;\">Repository Finder</span></a><span style=\"font-weight: 400;\"> tool hosted by </span><a href=\"https://datacite.org/\"><span style=\"font-weight: 400;\">DataCite</span></a><span style=\"font-weight: 400;\"> can help you find a repository listed in re3data.org. For assistance in making your dataset visible and easily accessible, contact your institution&rsquo;s library or reach out to the Portage DMP Coordinator at </span><a href=\"mailto:support@portagenetwork.ca\"><span style=\"font-weight: 400;\">support@portagenetwork.ca</span></a><span style=\"font-weight: 400;\">.&nbsp;</span></p>", "type"=>"guidance", "versionable_id"=>"ecde3cfd-0916-42f4-bd56-f78027907fa1"})
Section.create({"id"=>22181, "title"=>"Ethics and Intellectual Property", "description"=>nil, "number"=>5, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"6030c2d9-510a-4a63-9d40-9a56be11b3d3"})
Question.create({"id"=>68311, "text"=>"Are there any ethical or legal concerns related to your data that you will need to address? Are there any ownership or intellectual property concerns that could limit if/how you can share your research outputs?", "default_value"=>nil, "number"=>1, "section_id"=>22181, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"436857f9-9ca4-4d5f-9dba-4a3fcfd75cce"})
Annotation.create({"id"=>64056, "question_id"=>68311, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Consider the end-user license, and ownership or intellectual property rights of any secondary data or code you will use. Consider any data-sharing agreements you have signed, and repository &lsquo;terms of use&rsquo; you have agreed to. These may determine what end-user license you can apply to your own research outputs, and may limit if and how you can redistribute your research outputs. If you are working with an industry partner, will you have a process in place to manage that partnership, and an understanding of how the research outputs may be shared? Describe any foreseeable concerns or constraints here, if applicable.</span>", "type"=>"guidance", "versionable_id"=>"78e41338-2260-43d5-8f77-b4ceab751e6b"})
Section.create({"id"=>22182, "title"=>"Roles and Responsibilities", "description"=>nil, "number"=>6, "phase_id"=>3450, "modifiable"=>true, "versionable_id"=>"8266d425-9967-49b2-96dd-789b44af2ce7"})
Question.create({"id"=>68312, "text"=>"Identify who will be responsible for managing data during and after the project. Indicate the major data management tasks for which they will be responsible.", "default_value"=>nil, "number"=>1, "section_id"=>22182, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"b0d408b7-5f77-494b-8bf1-d3a1a21f4ab6"})
Annotation.create({"id"=>64057, "question_id"=>68312, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Identify who will be responsible -- individuals or organizations -- for carrying out these parts of your project. Consider including the time frame associated with these staff responsibilities, and document any training needed to prepare staff for data management duties.</span>", "type"=>"guidance", "versionable_id"=>"b1d340cb-6a7c-4184-acb8-ae00ee0d72e3"})
Question.create({"id"=>68313, "text"=>"How will responsibilities for managing data activities be handled if substantive changes happen in the personnel overseeing the project's data, including a change of Principal Investigator?", "default_value"=>nil, "number"=>2, "section_id"=>22182, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"63641555-6eab-43a0-8bad-d23e046af190"})
Annotation.create({"id"=>64058, "question_id"=>68313, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Indicate a succession strategy for management of these data if one or more people responsible for the data leaves (e.g. a graduate student leaving after graduation). Describe the process to follow if the Principal Investigator leaves the project. In some instances, a co-investigator or the department or division overseeing this research will assume responsibility.</span>", "type"=>"guidance", "versionable_id"=>"39b4484c-74c2-4e81-9380-83265c0f17c7"})
Question.create({"id"=>68314, "text"=>"What resources will you require to implement your data management plan? What do you estimate the overall cost for data management to be?", "default_value"=>nil, "number"=>3, "section_id"=>22182, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"f569c924-a28c-4850-be99-8023b7245a7b"})
Annotation.create({"id"=>64059, "question_id"=>68314, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">This estimate should incorporate data management costs incurred during the project as well as those required for the longer-term support for the data after the project has completed. Items to consider in the latter category of expenses include the costs of curating and providing long-term access to the data. It might include technical aspects of data management, training requirements, file storage &amp; backup, the computational environment or software needed to manage, analyze or visualize research data, and contributions of non-project staff. Read more about the costs of RDM and how these can be addressed in advance: &ldquo;</span><a href=\"https://zenodo.org/record/3837717#.XxHRAC0ZPUK\"><span style=\"font-weight: 400;\">What will it cost to manage and share my data?</span></a><span style=\"font-weight: 400;\">&rdquo; by OpenAIRE. There is also an online </span><a href=\"https://zingtree.com/host.php?style=buttons&amp;tree_id=511095771&amp;persist_names=Restart&amp;persist_node_ids=1&amp;start_node=1&amp;start_tree=511095771#1\"><span style=\"font-weight: 400;\">data management costing</span></a><span style=\"font-weight: 400;\"> tool available to help determine the costs and staffing requirements in your project proposal. </span>", "type"=>"guidance", "versionable_id"=>"c2c74cb7-7f7a-4011-84fc-9ef478f55426"})
Template.create!({"id"=>3472, "title"=>"Portage Template for Open Science Workflows", "description"=>"<p><span style=\"font-weight: 400;\">This template guides the writing of a research data management plan (DMP) with an open science/open scholarship workflow, which uses mixed social sciences research methods, producing quantitative as well as qualitative datasets. The questions emphasize data sharing and reuse throughout the project, not only at the final stage of publication. This DMP template will be most useful to researchers who are working in a multi-institutional partnership and who have already completed a funding application and an ethics review protocol.&nbsp; The DMP is a living document:</span> <span style=\"font-weight: 400;\">don&rsquo;t forget to revisit your DMP throughout the research project to update or review your responses.</span></p>\r\n<p><span style=\"font-weight: 400;\">Not all of these questions will apply to all research projects. We encourage you to respond to as many as possible but ultimately, you and your team have to decide which questions and answers apply to your workflow. </span></p>", "published"=>true, "org_id"=>8, "locale"=>"en-CA", "is_default"=>false, "version"=>0, "visibility"=>"publicly_visible", "customization_of"=>nil, "family_id"=>43432169, "archived"=>false, "links"=>{"funder"=>[], "sample_plan"=>[]}})
Phase.create({"id"=>3451, "title"=>"Portage Template for Open Science Workflows", "description"=>"<p><span style=\"font-weight: 400;\">This template guides the writing of a research data management plan (DMP) with an open science/open scholarship workflow, which uses mixed social sciences research methods, producing quantitative as well as qualitative datasets. The questions emphasize data sharing and reuse throughout the project, not only at the final stage of publication. This DMP template will be most useful to researchers who are working in a multi-institutional partnership and who have already completed a funding application and an ethics review protocol.&nbsp; The DMP is a living document:</span> <span style=\"font-weight: 400;\">don&rsquo;t forget to revisit your DMP throughout the research project to update or review your responses.</span></p>\r\n<p><span style=\"font-weight: 400;\">Not all of these questions will apply to all research projects. We encourage you to respond to as many as possible but ultimately, you and your team have to decide which questions and answers apply to your workflow. </span></p>", "number"=>1, "template_id"=>3472, "modifiable"=>true, "versionable_id"=>"e6951813-58f1-4b51-918d-5b29a744541d"})
Section.create({"id"=>22183, "title"=>"Responsibilities and Resources", "description"=>nil, "number"=>1, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"1e26e580-841e-4e88-b4f2-5678e833eb25"})
Question.create({"id"=>68315, "text"=>"Who will be responsible for data management? Will the Principal Investigator (PI) hold all responsibility during and beyond the project, or will this be divided among a team or partner organizations?", "default_value"=>nil, "number"=>1, "section_id"=>22183, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"87268b11-ca08-4ae3-9697-5744ca26fa3c"})
Annotation.create({"id"=>64060, "question_id"=>68315, "org_id"=>8, "text"=>"<p><strong>Assign responsibilities: </strong><span style=\"font-weight: 400;\">Once completed, your data management plan will outline important data activities in your project. Identify who will be responsible -- individuals or organizations -- for carrying out these parts of your data management plan. This could also include the time frame associated with these staff responsibilities and any training needed to prepare staff for these duties.</span></p>", "type"=>"guidance", "versionable_id"=>"b03f2ff9-d7fd-4aa2-866b-360022d6b0ec"})
Question.create({"id"=>68316, "text"=>"In the event that the PI leaves the project, who will replace them? Who will take temporary responsibility until a new PI takes over?", "default_value"=>nil, "number"=>2, "section_id"=>22183, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"15553fae-6358-45c9-8cfd-83eb58a75810"})
Annotation.create({"id"=>64061, "question_id"=>68316, "org_id"=>8, "text"=>"<p><strong>Succession planning: </strong><span style=\"font-weight: 400;\">The PI is usually in charge of maintaining data accessibility standards for the team. Consider who will field questions about accessing information or granting access to the data in&nbsp; the event the PI leaves the project. Usually the Co-PI takes over the responsibilities.</span></p>\r\n<p><span style=\"font-weight: 400;\">Indicate a succession strategy for these data in the event that one or more people responsible for the data leaves (e.g. a graduate student leaving after graduation). Describe the process to be followed in the event that the Principal Investigator leaves the project. In some instances, a co-investigator or the department or division overseeing this research will assume responsibility.</span></p>", "type"=>"guidance", "versionable_id"=>"f9d4e86d-aa97-48c9-9cbe-b8258e28564f"})
Question.create({"id"=>68317, "text"=>"List all expected resources for data management required to complete your project. What hardware, software and human resources will you need? What is your estimated budget?", "default_value"=>nil, "number"=>3, "section_id"=>22183, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"0d803857-de44-4cf2-89cc-cc9c3042880d"})
Annotation.create({"id"=>64062, "question_id"=>68317, "org_id"=>8, "text"=>"<strong>Budgeting: </strong><span style=\"font-weight: 400;\">Common purchases are hard drives, cloud storage or software access. </span><a href=\"https://zingtree.com/host.php?tree_id=511095771\"><span style=\"font-weight: 400;\">TU Delft&rsquo;s Data Management Costing Tool</span></a><span style=\"font-weight: 400;\"> is helpful to determine the share of human labor (FTE) that should be allocated for research data management.</span>", "type"=>"guidance", "versionable_id"=>"ebf2728e-23ab-4044-893c-e2eb6c26c8fc"})
Section.create({"id"=>22184, "title"=>"Data Collection", "description"=>nil, "number"=>2, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"be274f01-2b3b-4e22-9bdc-b66f5c5acab3"})
Question.create({"id"=>68318, "text"=>"What types of data will you collect, create, link to, acquire and/or record?", "default_value"=>nil, "number"=>1, "section_id"=>22184, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"dad52b46-dbd1-41e6-8781-fc37ffb9ca72"})
Annotation.create({"id"=>64063, "question_id"=>68318, "org_id"=>8, "text"=>"<p><strong>Data types:&nbsp;</strong><span style=\"font-weight: 400;\">Your research data may include digital resources, software code, audio files, image files, video, numeric, text, tabular data, modeling data, spatial data, instrumentation data.</span></p>", "type"=>"guidance", "versionable_id"=>"1a5210a5-50a1-4eb5-874e-ceaecf97e10f"})
Question.create({"id"=>68319, "text"=>"<span style=\"font-weight: 400;\">Answer the following regarding file formats:</span><br />\r\n<ol style=\"list-style-type: upper-alpha;\">\r\n<li><span style=\"font-weight: 400;\">What file formats do you expect to collect (e.g. .doc, .csv, .jpg, .mov)?</span></li>\r\n<li><span style=\"font-weight: 400;\">Are these file formats easy to share with other researchers from different disciplines? </span></li>\r\n<li><span style=\"font-weight: 400;\">In the event that one of your chosen file formats becomes obsolete (or is no longer supported) how will you ensure access to the research data? </span></li>\r\n<li><span style=\"font-weight: 400;\">Does your data need to be copied to a new media or cloud platform, or converted to a different file format when you store or publish your datasets?</span></li>\r\n</ol>", "default_value"=>nil, "number"=>2, "section_id"=>22184, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"badccb96-a80a-48f7-b9a0-16aa6415dabb"})
Annotation.create({"id"=>64064, "question_id"=>68319, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Proprietary file formats requiring specialized software or hardware to use are not recommended, but may be necessary for certain data collection or analysis methods. Using open file formats or industry-standard formats (e.g. those widely used by a given community) is preferred whenever possible. Read more about file formats: </span><a href=\"https://www.bac-lac.gc.ca/eng/services/government-information-resources/guidelines/Pages/guidelines-file-formats-transferring-information-resources-enduring-value.aspx\"><span style=\"font-weight: 400;\">Library and Archives Canada,</span></a><span style=\"font-weight: 400;\">&nbsp; </span><a href=\"https://researchdata.library.ubc.ca/plan/format-your-data/\"><span style=\"font-weight: 400;\">UBC Library</span></a><span style=\"font-weight: 400;\"> or </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/format.aspx\"><span style=\"font-weight: 400;\">UK Data Archive</span></a><span style=\"font-weight: 400;\">. </span>", "type"=>"guidance", "versionable_id"=>"375ed213-37ee-4a2b-a9f4-0e96984cbee0"})
Question.create({"id"=>68320, "text"=>"Answer the following regarding naming conventions:<br />\r\n<ol style=\"list-style-type: upper-alpha;\">\r\n<li><span style=\"font-weight: 400;\">How will you structure, name and version-control your files to help someone outside your research team understand how your data are organized?</span></li>\r\n<li><span style=\"font-weight: 400;\">Describe your ideal workflow for file sharing between research team members step-by-step. </span></li>\r\n<li><span style=\"font-weight: 400;\">What tools or strategies will you use to document your workflow as it evolves during the course of the project?</span></li>\r\n</ol>", "default_value"=>nil, "number"=>3, "section_id"=>22184, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"948d7f59-bc93-4545-9479-cd657c31f6c9"})
Annotation.create({"id"=>64065, "question_id"=>68320, "org_id"=>8, "text"=>"<strong>File naming and versioning: </strong>It is important to keep track of different copies or versions of files, files held in different formats or locations, and information cross-referenced between files. This process is called 'version control'. Logical file structures, informative naming conventions, and clear indications of file versions, all contribute to better use of your data during and after your research project. These practices will help ensure that you and your research team are using the appropriate version of your data, and minimize confusion regarding copies on different computers and/or on different media. Read more about file naming and version control from <a href=\"https://researchdata.library.ubc.ca/plan/organize-your-data/\">UBC Library</a> or the <a href=\"https://www.ukdataservice.ac.uk/manage-data/format/organising.aspx\">UK Data Service</a>.<br /><br />Remember that this workflow can be adapted, and the DMP updated throughout the project.<br /><br />Using a <a href=\"https://authors.library.caltech.edu/103626/1/FileNamingConventionWorksheet_Caltech.pdf\">file naming convention worksheet</a> can be very useful. Make sure the convention only uses alphanumeric characters, dashes and underscores. In general, file names should be 32 characters or less and contain the date and the version number (e.g.: &ldquo;P1-MUS023_2020-02-29_051_raw.tif&rdquo; and &ldquo;P1-MUS023_2020-02-29_051_clean_v1.tif&rdquo;). <br /><br /><strong>Document workflows: </strong>Have you thought about how you will capture, save and share your workflow and project milestones with your team? You can create an onboarding document to ensure that all team members adopt the same workflows or use workflow management tools like <a href=\"https://osf.io/\">OSF</a> or <a href=\"https://github.com/\">GitHub</a>.", "type"=>"guidance", "versionable_id"=>"8733d208-dda2-42c0-a251-15ddf102c92c"})
Section.create({"id"=>22185, "title"=>"Documentation and Metadata", "description"=>nil, "number"=>3, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"775b7fdb-83d0-492f-aa50-7c4922aaf08e"})
Question.create({"id"=>68321, "text"=>"What support material and documentation (e.g. ReadMe) will your team members and future researchers need in order to navigate and reuse your data without ambiguity?", "default_value"=>nil, "number"=>1, "section_id"=>22185, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"03d295b0-9582-41ce-996b-7304b837da5a"})
Annotation.create({"id"=>64066, "question_id"=>68321, "org_id"=>8, "text"=>"<p><strong>Data documentation:&nbsp;</strong><span style=\"font-weight: 400;\">It is strongly encouraged to include a ReadMe file with all datasets (or similar) to assist in understanding data collection and processing methods, naming conventions and file structure.</span></p>\r\n<span style=\"font-weight: 400;\">Typically, good data documentation includes information about the study, data-level descriptions, and any other contextual information required to make the data usable by other researchers. Other elements you should document, as applicable, include: research methodology used, variable definitions, vocabularies, classification systems, units of measurement, assumptions made, format and file type of the data, a description of the data capture and collection methods, explanation of data coding and analysis performed (including syntax files). View a useful template from Cornell University&rsquo;s </span><a href=\"https://data.research.cornell.edu/content/readme\"><span style=\"font-weight: 400;\">&ldquo;Guide to writing &lsquo;ReadMe&rsquo; style metadata&rdquo;</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"187f3a43-5898-4c79-8bad-d96b780ac81a"})
Question.create({"id"=>68322, "text"=>"How will you undertake documentation of data collection, processing and analysis, within your workflow to create consistent support material? Who will be responsible for this task?", "default_value"=>nil, "number"=>2, "section_id"=>22185, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"4e2a6a99-cd3e-4fc2-b6d0-4954312a7cac"})
Annotation.create({"id"=>64067, "question_id"=>68322, "org_id"=>8, "text"=>"<p><strong>Assign responsibilities for documentation:&nbsp;</strong><span style=\"font-weight: 400;\">Individual roles and workflows should include gathering data documentation as a key element.</span></p>\r\n<p><span style=\"font-weight: 400;\">Establishing responsibilities for data management and documentation is useful if you do it early on,&nbsp; ideally in advance of data collection and analysis, Some researchers use </span><a href=\"https://casrai.org/credit/\"><span style=\"font-weight: 400;\">CRediT taxonomy</span></a><span style=\"font-weight: 400;\"> to determine authorship roles at the beginning of each project. They can also be used to make team members responsible for proper data management and documentation.</span></p>\r\n<p><span style=\"font-weight: 400;\">Consider how you will capture this information and where it will be recorded, to ensure accuracy, consistency, and completeness of the documentation.</span></p>", "type"=>"guidance", "versionable_id"=>"9b51a4f3-1d0e-4099-87e3-d2170827ec09"})
Question.create({"id"=>68323, "text"=>"Do you plan to use a metadata standard? What specific schema might you use?", "default_value"=>nil, "number"=>3, "section_id"=>22185, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"27eb1a30-f4cc-49cc-8034-f1468e8b456d"})
Annotation.create({"id"=>64068, "question_id"=>68323, "org_id"=>8, "text"=>"<p><strong>Metadata for datasets:&nbsp;</strong><span style=\"font-weight: 400;\">DataCite has developed a </span><a href=\"https://schema.datacite.org/meta/kernel-4.3/\"><span style=\"font-weight: 400;\">metadata schema</span></a><span style=\"font-weight: 400;\"> specifically for open datasets. It lists a set of core metadata fields and instructions to make datasets easily identifiable and citable.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">There are many other general and domain-specific metadata standards.&nbsp; Dataset documentation should be provided in one of these standard, machine readable, openly-accessible formats to enable the effective exchange of information between users and systems.&nbsp; These standards are often based on language-independent data formats such as XML, RDF, and JSON. There are many metadata standards based on these formats, including discipline-specific standards. Read more about metadata standards at the </span><a href=\"http://www.dcc.ac.uk/resources/metadata-standards\"><span style=\"font-weight: 400;\">UK Digital Curation Centre's Disciplinary Metadata</span></a><span style=\"font-weight: 400;\"> resource.</span>", "type"=>"guidance", "versionable_id"=>"9d136659-7778-4399-a35e-2060093a21d2"})
Question.create({"id"=>68324, "text"=>"How will you make sure that a) your primary data collection methods are documented with transparency and b) your secondary data sources (i.e., data you did not collect yourself) &mdash; are easily identified and cited?", "default_value"=>nil, "number"=>4, "section_id"=>22185, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"27f81bb9-83f0-4dd7-8892-55a9116411aa"})
Annotation.create({"id"=>64069, "question_id"=>68324, "org_id"=>8, "text"=>"<p><strong>Document your process:&nbsp;</strong><span style=\"font-weight: 400;\">It is useful to consult regularly with members of the research team to capture potential changes in data collection/processing that need to be reflected in the documentation.</span></p>", "type"=>"guidance", "versionable_id"=>"7a05ca96-27ff-47f0-80c9-125b49dc3331"})
Section.create({"id"=>22186, "title"=>"Storage and Backup", "description"=>nil, "number"=>4, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"3d0731ee-491c-4b86-aa44-e517a5b4fb5d"})
Question.create({"id"=>68325, "text"=>"List your anticipated storage needs (e.g., hard drives, cloud storage, shared drives). List how long you intend to use each type and what capacities you may require.", "default_value"=>nil, "number"=>1, "section_id"=>22186, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"decb0dcb-e39f-4078-a969-3800219054bb"})
Annotation.create({"id"=>64070, "question_id"=>68325, "org_id"=>8, "text"=>"<p><strong>Estimating data storage needs:&nbsp;</strong><span style=\"font-weight: 400;\">Storage-space estimates should take into account requirements for file versioning, backups, and growth over time.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">If you are collecting data over a long period (e.g. several months or years), your data storage and backup strategy should accommodate data growth. Include your back-up storage media in your estimate. </span></p>", "type"=>"guidance", "versionable_id"=>"af08ba96-f6cb-40cf-9147-48eaf75fafc6"})
Question.create({"id"=>68326, "text"=>"What is your anticipated backup and storage schedule? How often will you save your data, in what formats, and where?", "default_value"=>nil, "number"=>2, "section_id"=>22186, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"0f0ae47e-ec8d-45a6-bf7f-ec290f386490"})
Annotation.create({"id"=>64071, "question_id"=>68326, "org_id"=>8, "text"=>"<p><strong>Follow the 3-2-1 rule to prevent data loss: </strong><span>It&rsquo;s important to have a regular backup schedule &mdash; and to document that process &mdash; so that you can review any changes to the data at any point during the project. The risk of losing data due to human error, natural disasters, or other mishaps can be mitigated by following the 3-2-1 backup rule: Have at least three copies of your data; store the copies on two different media; keep one backup copy offsite. Read more about storage and backup practices&nbsp; from the </span><a href=\"https://www.sheffield.ac.uk/library/rdm/storage\"><span>University of Sheffield Library</span></a><span> and the </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/store.aspx\"><span>UK Data Service</span></a><span>.</span></p>", "type"=>"guidance", "versionable_id"=>"4ec04d95-17c8-4b50-908b-f9a9b43cd779"})
Question.create({"id"=>68327, "text"=>"Keeping ethics protocol review requirements in mind, what is your intended storage timeframe for each type of data (raw, processed, clean, final) within your team? Will you also store software code or metadata?", "default_value"=>nil, "number"=>3, "section_id"=>22186, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"f8280b19-9bb9-4b57-833f-510956c12708"})
Annotation.create({"id"=>64072, "question_id"=>68327, "org_id"=>8, "text"=>"<p><strong>Ask for help:&nbsp;</strong><span style=\"font-weight: 400;\">Your institution should be able to provide guidance with local storage solutions. Seek out RDM support at your Library or your Advanced Research Computing department.</span><strong>&nbsp;</strong></p>\r\n<p><span style=\"font-weight: 400;\">Third-party commercial file sharing services (such as Google Drive and Dropbox) facilitate file exchange, but they are not necessarily permanent or secure, and servers are often located outside Canada. This may contravene ethics protocol requirements or other institutional policies.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">An ideal solution is one that facilitates cooperation and ensures data security, yet is able to be adopted by users with minimal training. Transmitting data between locations or within research teams can be challenging for data management infrastructure. Relying on email for data transfer is not a robust or secure solution.</span></p>\r\n<ul>\r\n<li><strong>Raw data</strong><span style=\"font-weight: 400;\"> are directly obtained from the instrument, simulation or survey.&nbsp;</span></li>\r\n<li><strong>Processed data</strong><span style=\"font-weight: 400;\"> result from some manipulation of the raw data in order to eliminate errors or outliers, to prepare the data for analysis, to derive new variables, or to de-identify the human participants.&nbsp;</span></li>\r\n<li><strong>Analyzed data</strong><span style=\"font-weight: 400;\"> are the results of qualitative, statistical, or mathematical analysis of the processed data. They can be presented as graphs, charts or statistical tables.&nbsp;</span></li>\r\n<li><strong>Final data</strong><span style=\"font-weight: 400;\"> are processed data that have, if needed, been converted into a preservation-friendly format.&nbsp;</span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"8b84516b-8918-4223-be51-7781ed722de6"})
Section.create({"id"=>22187, "title"=>"Sharing, Reuse, and Preservation", "description"=>nil, "number"=>5, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"6abbdb64-4d85-4c87-a052-d31966cfc1da"})
Question.create({"id"=>68328, "text"=>"How will your data (both raw and cleaned) be made accessible beyond the scope of the project and by researchers outside your team?", "default_value"=>nil, "number"=>1, "section_id"=>22187, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"a67381bd-a9cf-49aa-91db-2e82e7efda39"})
Annotation.create({"id"=>64073, "question_id"=>68328, "org_id"=>8, "text"=>"<p><strong>Help others reuse and cite your data:&nbsp;</strong><span style=\"font-weight: 400;\">Did you know that a dataset is a scholarly output that you can list on your CV, just like a journal article?&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">If you publish your data in a data repository (e.g., Zenodo, Dataverse, Dryad), it can be found and reused by others. Many repositories can issue unique Digital Object Identifiers (DOIs) which make it easier to identify and cite datasets.&nbsp;</span></p>\r\n<a href=\"https://www.re3data.org/\"><span style=\"font-weight: 400;\">re3data.org</span></a><span style=\"font-weight: 400;\"> is a directory of potential open data repositories. Consult with your colleagues to determine what repositories are common for data sharing in your field. You can also enquire about RDM support at your local institution, often in the Library or Advanced Research Computing. In the absence of local support, consult this</span> <a href=\"https://doi.org/10.5281/zenodo.3966349\"><span style=\"font-weight: 400;\">Portage repository options guide.</span></a>", "type"=>"guidance", "versionable_id"=>"4ad27a34-0c8b-4730-a447-3ba337decf31"})
Question.create({"id"=>68329, "text"=>"<span style=\"font-weight: 400;\">Is digital preservation a component of your project and do you need to plan for long-term archiving and preservation?</span>", "default_value"=>nil, "number"=>2, "section_id"=>22187, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"89ad4361-c4c5-48cd-bd57-99db52899d9d"})
Annotation.create({"id"=>64074, "question_id"=>68329, "org_id"=>8, "text"=>"<p><strong>How long should you keep your data? </strong><span style=\"font-weight: 400;\">The length of time that you will keep or share your data beyond the active phase of your research can be determined by external policies (e.g. funding agencies, journal publishers), or by an understanding of the enduring (historical) value of a given set of data. The need to publish data in the short-term (i.e. for peer-verification purposes), for a longer-term access for reuse (to comply with funding requirements), or for preservation through ongoing file conversion and migration (for data of lasting historical value), will influence the choice of data repository or archive.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">If you need long-term archiving for your data set, choose a&nbsp; preservation-capable repository. Digital preservation can be costly and time-consuming, and not all data can or should be preserved. &nbsp; </span></p>", "type"=>"guidance", "versionable_id"=>"007b95f4-b69a-4bf2-92b9-1b612a397ba0"})
Question.create({"id"=>68330, "text"=>"<span style=\"font-weight: 400;\">What data will you be sharing publicly and in what form (e.g. raw, processed, analyzed, final)?</span>", "default_value"=>nil, "number"=>3, "section_id"=>22187, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"2f75c637-1d73-42c3-a6e4-67004e3969e1"})
Annotation.create({"id"=>64075, "question_id"=>68330, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Consider which types of data need to be shared to meet institutional or funding requirements, and which data may be restricted because of confidentiality/privacy/intellectual property considerations.</span></p>", "type"=>"guidance", "versionable_id"=>"6544d7ab-0ace-4193-b027-f8df7230f81f"})
Question.create({"id"=>68331, "text"=>"<span style=\"font-weight: 400;\">Have you considered what type of end-user license to include with your data? </span>", "default_value"=>nil, "number"=>4, "section_id"=>22187, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"8619c8be-e6c3-4840-a476-b1253b2a0bc7"})
Annotation.create({"id"=>64076, "question_id"=>68331, "org_id"=>8, "text"=>"<p><strong>Use open licenses to promote data sharing and reuse:&nbsp;</strong><span style=\"font-weight: 400;\">Licenses determine what uses can be made of your data. Consider including a copy of your end-user license with your DMP.</span><strong>&nbsp;</strong></p>\r\n<p><span style=\"font-weight: 400;\">As the creator of a dataset (or any other academic or creative work) you usually hold its copyright by default. However, copyright prevents other researchers from reusing and building on your work. </span><a href=\"https://opendatacommons.org/licenses/\"><span style=\"font-weight: 400;\">Open Data Commons licenses</span></a><span style=\"font-weight: 400;\"> and </span><a href=\"https://creativecommons.org/about/cclicenses/\"><span style=\"font-weight: 400;\">Creative Commons licenses</span></a><span style=\"font-weight: 400;\"> are a free, simple and standardized way to grant copyright permissions and ensure proper attribution (i.e., citation). CC-BY is the most open license, and allows others to copy, distribute, remix and build upon your work &mdash;as long as they credit you or cite your work.</span></p>\r\n<p><span style=\"font-weight: 400;\">Even if you choose to make your data part of the public domain (with no restrictions on reuse), it is preferable to make this explicit by using a license such as </span><a href=\"https://creativecommons.org/publicdomain/zero/1.0/\"><span style=\"font-weight: 400;\">Creative Commons' CC0</span></a><span style=\"font-weight: 400;\">. It is strongly recommended to&nbsp; share your data openly using an Open Data or Creative Commons license.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">Learn more about data licensing at the </span><a href=\"http://www.dcc.ac.uk/resources/how-guides/license-research-data\"><span style=\"font-weight: 400;\">Digital Curation Centre</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"9a2e4242-63b6-491f-a3e1-9ebefe008b12"})
Question.create({"id"=>68332, "text"=>"What tools and strategies will you take to promote your research? How will you let the research community and the public know that your data exists and is ready to be reused?", "default_value"=>nil, "number"=>5, "section_id"=>22187, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"e48161b9-d263-4e61-a945-600df814d862"})
Annotation.create({"id"=>64077, "question_id"=>68332, "org_id"=>8, "text"=>"<p><strong>Data sharing as knowledge mobilization:&nbsp;</strong><span style=\"font-weight: 400;\">Using social media, e-newsletters, bulletin boards, posters, talks, webinars, discussion boards or discipline-specific forums are good ways to gain visibility, promote transparency and encourage data discovery and reuse.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">One of the best ways to refer other researchers to your deposited datasets is to cite them the same way you cite other types of publications. Publish your data in a repository that will assign a persistent identifier (such as a DOI) to your dataset. This will ensure a stable access to the dataset and make it retrievable by various discovery tools. Some repositories also create links from datasets to their associated papers, increasing the visibility of the publications. </span></p>", "type"=>"guidance", "versionable_id"=>"c7f39a7c-4029-4d82-b80a-06bd074d3e7c"})
Section.create({"id"=>22188, "title"=>"Ethics and Legal Compliance", "description"=>nil, "number"=>6, "phase_id"=>3451, "modifiable"=>true, "versionable_id"=>"278c3695-21ad-434e-b1fb-922da1c5959f"})
Question.create({"id"=>68333, "text"=>"<p>Are there institutional, governmental or legal policies that you need to comply with in regards to your data standards?</p>", "default_value"=>nil, "number"=>1, "section_id"=>22188, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"312c8b7c-0251-4d16-af9b-d0400d78143b"})
Annotation.create({"id"=>64078, "question_id"=>68333, "org_id"=>8, "text"=>"<p><strong>Determine jurisdiction: </strong><span style=\"font-weight: 400;\">If your study is cross-institutional or international, you&rsquo;ll need to determine which laws and policies will apply to your research.</span></p>\r\n<p><span style=\"font-weight: 400;\">Compliance with privacy legislation and laws that may impose content restrictions in the data should be discussed with your institution's privacy officer or research services office.</span></p>\r\n<p><span style=\"font-weight: 400;\">If you collaborate with a partner in the European Union, for example,&nbsp; you might need to follow the </span><a href=\"https://en.wikipedia.org/wiki/General_Data_Protection_Regulation\"><span style=\"font-weight: 400;\">General Data Protection Regulation (GDPR)</span></a><span style=\"font-weight: 400;\">.</span></p>\r\n<p><span style=\"font-weight: 400;\">If you are working with data that has First Nations, M&eacute;tis, or Inuit ownership, for example, you will need to work with protocols that ensure community privacy is respected and community harm is reduced. For further guidance on Indigenous data sovereignty, see </span><a href=\"https://fnigc.ca/ocap-training/\"><span style=\"font-weight: 400;\">OCAP Principles</span></a><span style=\"font-weight: 400;\">, and in a global&nbsp; context, </span><a href=\"https://www.gida-global.org/care\"><span style=\"font-weight: 400;\">CARE Principles of Indigenous Data Governance</span></a><span style=\"font-weight: 400;\">.</span></p>", "type"=>"guidance", "versionable_id"=>"cf964f60-e118-45de-bcd8-1d6c5e0848c3"})
Question.create({"id"=>68334, "text"=>"Will you encounter protected or personally-identifiable information in your research? If so, how will you make sure it stays secure and is accessed by approved team members only?", "default_value"=>nil, "number"=>2, "section_id"=>22188, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"d853d0f4-fa52-4820-89c0-e98a8af65d69"})
Annotation.create({"id"=>64079, "question_id"=>68334, "org_id"=>8, "text"=>"<p><strong>Get informed consent before you collect data:&nbsp;</strong><span style=\"font-weight: 400;\">Obtaining the appropriate consent from research participants is an important step in assuring Research Ethics Boards that the data may be shared with researchers outside your project. Your informed consent statement may identify certain conditions clarifying the uses of the data by other researchers. For example, your statement may stipulate that the data will either only be shared for non-profit research purposes (you can use CC-by-NC &mdash; the non-commercial Creative Commons licence with attribution) or that the data will not be linked with other personally-identifying data. Note that this aspect is not covered by an open license. You can learn more about data security at the </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/store.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span></p>\r\n<p><span style=\"font-weight: 400;\">Inform your study participants if you intend to publish an anonymized and de-identified version of collected data, and that by participating, they agree to these terms. For sample language for informed consent: </span><a href=\"https://www.icpsr.umich.edu/web/pages/datamanagement/confidentiality/conf-language.html\"><span style=\"font-weight: 400;\">ICPSR Recommended Informed Consent Language for Data Sharing</span></a>.</p>\r\n<p><span style=\"font-weight: 400;\">You may need to consider strategies to ensure the ethical reuse of your published dataset by new researchers. These strategies may affect your selection of a suitable license, and in some cases you may not be able to use an open license.</span></p>", "type"=>"guidance", "versionable_id"=>"22256257-2b6a-4f7a-8e36-b7ca94259be1"})
Question.create({"id"=>68335, "text"=>"<span style=\"font-weight: 400;\">Before publishing or otherwise sharing a dataset are you required to obscure identifiable data (name, gender, date of birth, etc), in accordance with your jurisdiction's laws, or your ethics protocol? Are there any time restrictions for when data can be publicly accessible?</span>", "default_value"=>nil, "number"=>3, "section_id"=>22188, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>nil, "versionable_id"=>"6c519e0c-e338-4edc-bb2c-0363a086e4ff"})
Annotation.create({"id"=>64080, "question_id"=>68335, "org_id"=>8, "text"=>"<p><strong>Privacy protection:&nbsp;</strong><span style=\"font-weight: 400;\">Open science workflows prioritize being &ldquo;as open as possible and as closed as necessary.&rdquo; Think about any privacy concerns you may have in regards to your data, or other restrictions on access outlined in your ethics protocol. If your institution or funder regulates legal or ethical guidelines on what information must be protected, take a moment to verify you have complied with the terms for consent of sharing data. In the absence of local support for anonymization or de-identification of data, you can reach out to the Portage DMP Coordinator at <a href=\"mailto:support@portagenetwork.ca\">support@portagenetwork.ca</a>. </span><span style=\"font-weight: 400;\">&nbsp;</span></p>", "type"=>"guidance", "versionable_id"=>"f0fb40be-45b6-42b6-b2b6-8dd8c63d7c91"})
Template.create!({"id"=>3473, "title"=>"Portage Template for Neuroimaging in the Neurosciences", "description"=>"<p><span style=\"font-weight: 400;\">This Neuroimaging data management plan (DMP) template is designed to be completed in two phases: Phase 1 questions probe at a high-level, seeking information about the general direction of the study. Normally, researchers will be able to respond to phase 1 questions at the outset of a project.&nbsp;&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">Phase 2 questions seek greater detail. It is understood that these answers will often depend on the outcome of several steps in the research project, such as: a literature review, imaging protocol design and experimental design, or running multiple pilot subjects and interpreting the outcome. As these details become known, the DMP can and should be revisited. This approach underscores that DMPs are living documents that evolve throughout a research project.&nbsp; </span></p>", "published"=>true, "org_id"=>8, "locale"=>"en-CA", "is_default"=>false, "version"=>0, "visibility"=>"publicly_visible", "customization_of"=>nil, "family_id"=>1659720835, "archived"=>false, "links"=>{"funder"=>[], "sample_plan"=>[]}})
