Section.create({"id"=>22523, "title"=>"Data Collection", "description"=>nil, "number"=>1, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"15e93d45-fc9f-4e85-a2c9-da7751a9781d"})
Question.create({"id"=>69360, "text"=>"Why are you collecting or generating your data?", "default_value"=>nil, "number"=>1, "section_id"=>22523, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"67eed90a-668b-4bca-ab18-58f957cee32c"})
Annotation.create({"id"=>65224, "question_id"=>69360, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Describe the purpose or goal of this project. Is the data collection for a specific study or part of a long-term collection effort? What is the relationship between the data you are collecting and any existing data? Note existing data structure and procedures if building on previous work.</span>", "type"=>"guidance", "versionable_id"=>"be721245-2846-4178-b766-861e5c850b5b"})
Question.create({"id"=>69361, "text"=>"What types of data will you collect, create, link to, acquire and/or record? Please be specific, including:", "default_value"=>nil, "number"=>2, "section_id"=>22523, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"3202cea2-c97b-486b-83a2-4e0d6333e3dc"})
Annotation.create({"id"=>65225, "question_id"=>69361, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Types of data you may create or capture could include: geospatial layers (shapefiles; may include observations, models, remote sensing, etc.); tabular observational data; field, laboratory, or experimental data; numerical model input data and outputs from numerical models; images; photographs; video.</span>", "type"=>"example_answer", "versionable_id"=>"33df6e31-d265-407e-abd3-eee758360891"})
Annotation.create({"id"=>65226, "question_id"=>69361, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Please also describe the tools and methods that you will use to collect or generate the data. Outline the procedures that must be followed when using these tools to ensure consistent data collection or generation. If possible, include any sampling procedures or modelling techniques you will use to collect or generate your data. </span></p>", "type"=>"guidance", "versionable_id"=>"63e9565a-f7f5-4744-92be-bcb6fd752ef9"})
Question.create({"id"=>69362, "text"=>"<p>Where are you collecting or generating your data (i.e., study area)? Include as appropriate the spatial boundaries, water source type and watershed name.</p>", "default_value"=>nil, "number"=>3, "section_id"=>22523, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"0ae45d15-9459-482c-bdb5-3fcc91bd5efc"})
Annotation.create({"id"=>65227, "question_id"=>69362, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Try to use pre-existing collection standards, such as the </span><a href=\"http://beta-static.fishersci.com/content/dam/fishersci/en_CA/documents/brochures-and-catalogs/catalogs/ccme-protocols-manual-water-quality-sampling.pdf\"><span style=\"font-weight: 400;\">CCME&rsquo;s Protocols for Water Quality Sampling in Canada</span></a><span style=\"font-weight: 400;\">, whenever possible.&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">If you will set up monitoring station(s) to continuously collect or sample water quality data, please review resources such as the </span><a href=\"https://library.wmo.int/doc_num.php?explnum_id=7821\"><span style=\"font-weight: 400;\">World Meteorological Organization&rsquo;s technical report on Water Quality Monitoring</span></a><span style=\"font-weight: 400;\"> and</span> <a href=\"https://ccme.ca/en/current-activities/canadian-environmental-quality-guidelines\"><span style=\"font-weight: 400;\">CCME&rsquo;s Protocols for Water Quality Guidelines in Canada</span></a><span style=\"font-weight: 400;\">.</span></p>", "type"=>"guidance", "versionable_id"=>"2272baa6-458f-469e-886c-b9b785d0f0d4"})
Question.create({"id"=>69363, "text"=>"Are you using third party data? If so, describe the source of the data including the owner, database or repository DOIs or accession numbers.", "default_value"=>nil, "number"=>4, "section_id"=>22523, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"fb845b73-ae02-4d18-9c92-df383dde46bd"})
Annotation.create({"id"=>65228, "question_id"=>69363, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Include full references or links to the data when possible. Identify any license or use restrictions and ensure that you understand the policies for permitted use, redistribution and derived products.</span>", "type"=>"guidance", "versionable_id"=>"a5495f08-461c-4af9-998d-f95ec2fbf281"})
Section.create({"id"=>22524, "title"=>"Ethics and Legal Compliance", "description"=>nil, "number"=>2, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"0c571421-5307-4fa9-bb80-eabb35d343f4"})
Question.create({"id"=>69364, "text"=>"Does your project include sensitive data?", "default_value"=>nil, "number"=>1, "section_id"=>22524, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"a2a9fa25-176b-4d44-a3c8-152e18b3ccf6"})
Annotation.create({"id"=>65229, "question_id"=>69364, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Sensitive data is data that carries some risk with its collection. Examples of sensitive data include:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data governed by third party agreements, contracts or legislation.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Personal information, health related data, biological samples, etc.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Indigenous knowledge, and data collected from Indigenous peoples, or Indigenous lands, water and ice.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data collected with an industry partner.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Location information of species at risk.&nbsp;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data collected on private property, for example where identification of contaminated wells could impact property values or stigmatize residents or land owners.</span></li>\r\n</ul>\r\n<span style=\"font-weight: 400;\">Additional sensitivity assessment can be made using data classification matrices such as the </span><a href=\"https://www.usask.ca/avp-ict/documents/Data%20Classification%20Summary.pdf\"><span style=\"font-weight: 400;\">University of Saskatchewan Data Classification</span></a><span style=\"font-weight: 400;\"> guidance.</span>", "type"=>"guidance", "versionable_id"=>"8118e66e-1b55-4912-88d2-8640096bbdc4"})
Question.create({"id"=>69365, "text"=>"If your project includes sensitive data, how will you ensure that it is securely managed and accessible only to approved members of the project?", "default_value"=>nil, "number"=>2, "section_id"=>22524, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"0544c37f-2051-41dd-9b81-5c22f2665382"})
Annotation.create({"id"=>65230, "question_id"=>69365, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Methods used to share data will be dependent on the type, size, complexity and degree of sensitivity of data. Outline any problems anticipated in sharing data, along with causes and possible measures to mitigate these. Problems may include confidentiality, lack of consent agreements, or concerns about Intellectual Property Rights, among others.</span></p>\r\n<p><span style=\"font-weight: 400;\">Decisions should align with Research Ethics Board requirements. If you are collecting water quality data from Indigenous communities, please also review resources such as the Tri-Council Policy Statement (TCPS2) - Chapter 9:</span> <a href=\"https://ethics.gc.ca/eng/tcps2-eptc2_2018_chapter9-chapitre9.html\"><span style=\"font-weight: 400;\">Research Involving the First Nations, Inuit and M&eacute;tis Peoples of Canada</span></a><span style=\"font-weight: 400;\">, the <a href=\"https://www.afn.ca/uploads/files/nihbforum/info_and_privacy_doc-ocap.pdf\">First Nations </a></span><a href=\"https://www.afn.ca/uploads/files/nihbforum/info_and_privacy_doc-ocap.pdf\"><span style=\"font-weight: 400;\">Principles of OCAP</span></a><span style=\"font-weight: 400;\">, the </span><a href=\"https://www.gida-global.org/care\"><span style=\"font-weight: 400;\">CARE Principles of Indigenous Data Governance</span></a><span style=\"font-weight: 400;\">, the </span><a href=\"https://www.itk.ca/wp-content/uploads/2020/10/ITK-National-Inuit-Strategy-on-Research.pdf\"><span style=\"font-weight: 400;\">National Inuit Strategy on Researc</span><span style=\"font-weight: 400;\">h</span></a><span style=\"font-weight: 400;\">, and </span><a href=\"https://www.nri.nu.ca/sites/default/files/public/files/06-068%20ITK%20NRR%20booklet.pdf\"><span style=\"font-weight: 400;\">Negotiating Research Relationships with Inuit Communities</span></a><span style=\"font-weight: 400;\"> as appropriate.&nbsp;</span></p>\r\n<span style=\"font-weight: 400;\">Restrictions can be imposed by limiting physical access to storage devices, placing data on computers with no access to the Internet, through password protection, and by encrypting files. Sensitive data should never be shared via email or cloud storage services such as Dropbox. Read more about data security here: </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/store.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">. </span>", "type"=>"guidance", "versionable_id"=>"8bd94c7f-21de-4184-b266-af91563a45aa"})
Question.create({"id"=>69366, "text"=>"If applicable, what strategies will you undertake to address secondary uses of sensitive data?", "default_value"=>nil, "number"=>3, "section_id"=>22524, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"c9369fc0-bbee-4892-a1c2-e6a82db29cf8"})
Annotation.create({"id"=>65231, "question_id"=>69366, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Consider where, how, and to whom sensitive data with acknowledged long-term value should be made available, and for how long it should be archived. If you must restrict some data from sharing, consider making the metadata (information about the dataset) available in a public metadata catalogue.</span></p>\r\n<p><span style=\"font-weight: 400;\">Obtaining the appropriate consent from research participants is an important step in assuring Research Ethics Boards that the data may be shared with researchers outside your project. The consent statement may identify certain conditions clarifying the uses of your data by other researchers. For example, it may stipulate that the data will only be shared for non-profit research purposes or that the data will not be linked with personally identified data from other sources. It is important to consider how the data you are collecting may contribute to future research prior to obtaining research ethics approval since consent will dictate how the data can be used in the immediate study and in perpetuity.</span></p>", "type"=>"guidance", "versionable_id"=>"ab0ed679-5fff-472a-af52-324a8d76fac1"})
Question.create({"id"=>69367, "text"=>"How will you manage other legal, ethical, and intellectual property issues?", "default_value"=>nil, "number"=>4, "section_id"=>22524, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"82596e39-4cc6-46aa-8dcc-a075f224ca1f"})
Annotation.create({"id"=>65232, "question_id"=>69367, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Compliance with privacy legislation and laws that may impose content restrictions in the data should be discussed with your institution's privacy officer or research services office. Research Ethics Boards are also central to the research process.</span></p>\r\n<p><span style=\"font-weight: 400;\">Describe ownership, licensing, and any intellectual property rights associated with the data. Check your institution's policies for additional guidance in these areas. The University of Waterloo has a good example of an institutional policy on </span><a href=\"https://uwaterloo.ca/secretariat/policies-procedures-guidelines/policies/policy-73-intellectual-property-rights\"><span style=\"font-weight: 400;\">Intellectual Property Rights</span></a><span style=\"font-weight: 400;\">.</span></p>\r\n<p><span style=\"font-weight: 400;\">Terms of reuse must be clearly stated, in line with the relevant legal and ethical requirements where applicable (e.g., subject consent, permissions, restrictions, etc.). </span></p>", "type"=>"guidance", "versionable_id"=>"262b3f56-cfb7-4f90-af00-5d573d10b655"})
Section.create({"id"=>22525, "title"=>"File Management", "description"=>nil, "number"=>3, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"2c30a63a-1c2f-4272-aa1d-11847a4c9739"})
Question.create({"id"=>69368, "text"=>"What file formats will your data be in? Will these formats allow for data re-use, sharing and long-term access to the data? If not, how will you convert these into interoperable formats?", "default_value"=>nil, "number"=>1, "section_id"=>22525, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"af28a425-2068-4230-8af6-d6014601928e"})
Annotation.create({"id"=>65233, "question_id"=>69368, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Data should be collected and stored using machine readable, non-proprietary formats, such as .csv, .json, or .tiff. Proprietary file formats requiring specialized software or hardware to use are not recommended, but may be necessary for certain data collection or instrument analysis methods. If a proprietary format </span><em><span style=\"font-weight: 400;\">must</span></em><span style=\"font-weight: 400;\"> be used, can it be converted to an open format or accessed using free and open source tools?<br /></span><br />Using open file formats or industry-standard formats (e.g. those widely used by a given community) is preferred whenever possible. Read more about file formats: <a href=\"https://researchdata.library.ubc.ca/plan/format-your-data/\"><span style=\"font-weight: 400;\">UBC Library</span></a><span style=\"font-weight: 400;\">,</span> <a href=\"https://www.usgs.gov/products/data-and-tools/data-management/file-formats\"><span style=\"font-weight: 400;\">USGS</span></a><span style=\"font-weight: 400;\">, </span><a href=\"https://www.dataone.org/best-practices/document-and-store-data-using-stable-file-formats\"><span style=\"font-weight: 400;\">DataONE</span></a><span style=\"font-weight: 400;\">, or </span><a href=\"https://www.ukdataservice.ac.uk/manage-data/format.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"33219c57-1ce1-4382-b315-3a1d189521a4"})
Question.create({"id"=>69369, "text"=>"<p>What conventions and procedures will you use to structure, name and version-control your files to help you and others better understand how your data are organized?</p>", "default_value"=>nil, "number"=>2, "section_id"=>22525, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"60c43eee-c012-45f0-9193-065bba52330a"})
Annotation.create({"id"=>65234, "question_id"=>69369, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">File names should:</span></p>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">Clearly identify the name of the project (Unique identifier, Project Abbreviation), timeline (use the </span><a href=\"https://www.iso.org/iso-8601-date-and-time-format.html\">ISO date standard</a>) and type of data in the file;</li>\r\n<li>Avoid use of special characters, such as&nbsp; $ % ^ &amp; # | :, to prevent errors and use an underscore ( _)&nbsp; or dash (-) rather than spaces;</li>\r\n<li><span style=\"font-weight: 400;\">Be as concise as possible. Some instruments may limit you to specific characters. Check with your labs for any naming Standard Operating Procedures (SOPs) or requirements.&nbsp;</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400;\">Data Structure:</span></p>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">It is important to keep track of different copies or versions of files, files held in different formats or locations, and information cross-referenced between files. This process is called 'version control'. For versioning, the format of vMajor.Minor.Patch should be used (i.e. v1.1.0);</span></li>\r\n<li>Logical file structures, informative naming conventions and clear indications of file versions all contribute to better use of your data during and after your research project.&nbsp; These practices will help ensure that you and your research team are using the appropriate version of your data and minimize confusion regarding copies on different computers and/or on different media.&nbsp;</li>\r\n</ul>\r\n<p><span>Read more about file naming and version control:</span> <a href=\"https://researchdata.library.ubc.ca/plan/organize-your-data/\"><span>UBC Library</span></a> <span>or</span> <a href=\"https://www.ukdataservice.ac.uk/manage-data/format/organising.aspx\"><span>UK Data Service</span></a><span>.</span></p>", "type"=>"guidance", "versionable_id"=>"724d59ab-d887-406f-9491-d4028d3a645f"})
Section.create({"id"=>22526, "title"=>"Documentation and Metadata", "description"=>nil, "number"=>4, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"840ef947-99d8-42f7-bd29-2d36886cdd81"})
Question.create({"id"=>69370, "text"=>"What documentation will be needed for the data to be read and interpreted correctly in the future?", "default_value"=>nil, "number"=>1, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"e1d28f9a-2909-45c8-8e61-aa512f9dc073"})
Annotation.create({"id"=>65235, "question_id"=>69370, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Typically, good documentation includes information about the study, data-level descriptions and any other contextual information required to make the data usable by other researchers. Elements to document, as applicable, include: research methodology, variable definitions, vocabularies, classification systems, units of measurement, assumptions made, format and file type of the data, and details of who has worked on the project and performed each task, etc.&nbsp;&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />A readme file describing your formatting, naming conventions and procedures can be used to promote use and facilitate adherence to data policies. For instance, describe the names or naming process used for your study sites.</span><br /><span style=\"font-weight: 400;\"><br />Verify the spelling of study site names using the</span> <a href=\"https://www.nrcan.gc.ca/earth-sciences/geography/querying-canadian-geographical-names-database/9170\"><span style=\"font-weight: 400;\">Canadian Geographical Names Database</span></a><span style=\"font-weight: 400;\">.&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />If your data will be collected on Indigenous lands, ensure your naming scheme follows the naming conventions determined by the community.</span>", "type"=>"guidance", "versionable_id"=>"d7d92cb2-d7dd-480d-93b6-b98ce41c1e40"})
Question.create({"id"=>69371, "text"=>"<p>How will you describe samples collected?</p>", "default_value"=>nil, "number"=>2, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"7d973add-2f5f-4869-a46d-91b0bd7506cd"})
Annotation.create({"id"=>65236, "question_id"=>69371, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Include descriptions of sampling procedures and hardware or software used for data collection, including make, model and version where applicable. </span><strong>Sample and replicate labels</strong><span style=\"font-weight: 400;\"> should have a consistent format (sample number, name, field site, date of collection, analysis requested and preservatives added, if applicable) and a corresponding document with descriptions of any codes or short forms used.&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />For examples and guidelines, see the </span><a href=\"http://beta-static.fishersci.com/content/dam/fishersci/en_CA/documents/brochures-and-catalogs/catalogs/ccme-protocols-manual-water-quality-sampling.pdf\"><span style=\"font-weight: 400;\">CCME Protocols Manual for Water Quality Sampling in Canada</span></a> <span style=\"font-weight: 400;\">(taxonomy example p. 11, general guidelines p. 32-33).&nbsp;<br /></span><br /><span style=\"font-weight: 400;\">Consistency, relevance and cost-efficiency are key factors of sample collection and depend on the scope of the project. For practical considerations, see &ldquo;4.3 Step 3. Optimizing Data Collection and Data Quality&rdquo; in the </span><a href=\"https://ccme.ca/en/res/guidancemanualforoptimizingwaterqualitymonitoringprogramdesign_1.0_e.pdf\"><span style=\"font-weight: 400;\">CCME Guidance Manual for Optimizing Water Quality Monitoring Program Design</span></a><span style=\"font-weight: 400;\">. </span></p>", "type"=>"guidance", "versionable_id"=>"aa17caa0-e78b-44ed-8521-6419a256992c"})
Question.create({"id"=>69372, "text"=>"<p>How will you analyze and interpret the water quality data?</p>", "default_value"=>nil, "number"=>3, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"f2239dc5-45e2-4b8e-8eac-f4007daeff46"})
Annotation.create({"id"=>65237, "question_id"=>69372, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">It is important to have a standardized data analysis procedure and metadata detailing both the collection and analysis of the data. For guidance see &ldquo;4.4 Step 4. Data Analysis, Interpretation and Evaluation&rdquo; in the </span><a href=\"https://ccme.ca/en/res/guidancemanualforoptimizingwaterqualitymonitoringprogramdesign_1.0_e.pdf\"><span style=\"font-weight: 400;\">CCME Guidance Manual for Optimizing Water Quality Monitoring Program Design</span></a><span style=\"font-weight: 400;\">.</span><span style=\"font-weight: 400;\">&nbsp;<br /></span><br /><span style=\"font-weight: 400;\">If you will collect or analyze data as part of a wider program, such as the </span><a href=\"https://www.canada.ca/en/environment-climate-change/services/canadian-aquatic-biomonitoring-network/database.html\"><span style=\"font-weight: 400;\">Canadian Aquatic Biomonitoring Network</span></a><span style=\"font-weight: 400;\">, include links to the appropriate guidance documents.</span></p>", "type"=>"guidance", "versionable_id"=>"f4c447b1-3eec-41c5-9531-6564599b5bf1"})
Question.create({"id"=>69373, "text"=>"<p>What kind of Quality Assurance/Quality Control procedures are you planning to do?</p>", "default_value"=>nil, "number"=>4, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"6fb37f0d-2728-446f-bb7b-8265dc43ea4a"})
Annotation.create({"id"=>65238, "question_id"=>69373, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Include documentation about what QA/QC procedures will be performed. For guidance see &ldquo;1.3 QUALITY ASSURANCE/CONTROL IN SAMPLING&rdquo; in the </span><a href=\"https://ccme.ca/en/res/protocolsdocument_e_-final1.0correctedisbn-secure.pdf\"><span style=\"font-weight: 400;\">CCME </span><span style=\"font-weight: 400;\">Integrated guidance manual of sampling protocols for water quality monitoring in Canada</span></a> <span style=\"font-weight: 400;\">or the </span><a href=\"https://pubs.usgs.gov/of/2018/1018/ofr20181018.pdf\"><span style=\"font-weight: 400;\">Quality-Control Design for Surface-Water Sampling in the National Water-Quality Network</span></a><span style=\"font-weight: 400;\"> from the USGS.</span></p>", "type"=>"guidance", "versionable_id"=>"e884ea5b-f3b7-4037-943a-bb922a3b035f"})
Question.create({"id"=>69374, "text"=>"How will you make sure that documentation is created or captured consistently throughout your project?", "default_value"=>nil, "number"=>5, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"7b00c0b5-cb80-4df1-b2ed-70f98d79db2f"})
Annotation.create({"id"=>65239, "question_id"=>69374, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Consider how you will capture this information and where it will be recorded, ideally in advance of data collection and analysis, to ensure accuracy, consistency, and completeness of the documentation. Writing guidelines or instructions for the documentation process will enhance adoption and consistency among contributors. </span><span style=\"font-weight: 400;\">Often, resources you have already created can contribute to this (e.g., laboratory Standard Operating Procedures (SOPs), recommended textbooks,&nbsp; publications, websites, progress reports, etc.).&nbsp;&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />It is useful to consult regularly with the members of the research team to capture potential changes in data collection or processing that need to be reflected in the documentation. Individual roles and workflows should include gathering data documentation as a key element. </span><span style=\"font-weight: 400;\">Researchers should audit their documentation at a specific time interval (e.g., bi-weekly) to ensure documentation is created properly and information is captured consistently throughout the project. </span>", "type"=>"guidance", "versionable_id"=>"bb152fb9-7b27-498e-a53b-924870710526"})
Question.create({"id"=>69375, "text"=>"<p>List any metadata standard(s) and/or tools you will use to document and describe your data:</p>", "default_value"=>nil, "number"=>6, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"d01d2655-31d5-49a1-88a0-64caf9dd6529"})
Annotation.create({"id"=>65240, "question_id"=>69375, "org_id"=>8, "text"=>"<p><strong>Water Quality metadata standards</strong>:</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://github.com/gordonfn/schema\"><span style=\"font-weight: 400;\">DS-WQX</span></a><span style=\"font-weight: 400;\">: A schema designed for data entered into the DataStream repository based off of the US EPA&rsquo;s WQX standard.</span></li>\r\n<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://www.epa.gov/waterdata/water-quality-data-wqx\"><span style=\"font-weight: 400;\">WQX</span></a><span style=\"font-weight: 400;\">: Data model designed by the US EPA and USGS for upload to their water quality exchange portal.</span></li>\r\n</ul>\r\n<p><strong>Ecological metadata standards</strong>:</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://eml.ecoinformatics.org\"><span style=\"font-weight: 400;\">Ecological Metadata Language (EML)</span></a><span style=\"font-weight: 400;\">: A comprehensive vocabulary and a readable XML markup syntax for documenting research data.</span></li>\r\n</ul>\r\n<p><strong>Geographic metadata standards</strong>:</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\" aria-level=\"1\"><a href=\"https://www.dcc.ac.uk/resources/metadata-standards/iso-19115\"><span style=\"font-weight: 400;\">ISO 19115</span></a><span style=\"font-weight: 400;\">: International Standards Organisation&rsquo;s schema for describing geographic information and services.</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400;\">Read more about metadata standards:</span> <a href=\"http://www.dcc.ac.uk/resources/metadata-standards\"><span style=\"font-weight: 400;\">UK Digital Curation Centre's Disciplinary Metadata</span></a><span style=\"font-weight: 400;\">.</span></p>", "type"=>"example_answer", "versionable_id"=>"d2a93ca3-2303-49ec-842e-baf6d577e5a5"})
Annotation.create({"id"=>65241, "question_id"=>69375, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Metadata describes a dataset and provides vital information such as owner, description, keywords, etc. that allow data to be shared and discovered effectively. Researchers are encouraged to adopt commonly used and interoperable metadata schemas (general or domain-specific), which focus on the exchange of data via open spatial standards. Dataset documentation should be provided in a standard, machine readable, openly-accessible format to enable the effective exchange of information between users and systems.&nbsp; </span></p>", "type"=>"guidance", "versionable_id"=>"6740bdb4-ef76-4f1d-98fe-f37786af661e"})
Question.create({"id"=>69376, "text"=>"If the metadata standard will be modified, please explain how you will modify the standard to meet your needs.", "default_value"=>nil, "number"=>7, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"e85efaa9-5617-4edd-8c6d-85de20c2d03d"})
Annotation.create({"id"=>65242, "question_id"=>69376, "org_id"=>8, "text"=>"Deviation from existing metadata standards should only occur when necessary. If this is the case, please document these deviations so that others can recreate your process.", "type"=>"guidance", "versionable_id"=>"884d4577-d7a3-40c4-9bab-e9d47c1eca1b"})
Question.create({"id"=>69377, "text"=>"How will you make sure that metadata is created or captured consistently throughout your project?", "default_value"=>nil, "number"=>8, "section_id"=>22526, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"b828b1b7-c349-4a2b-a550-beea4bc788e2"})
Annotation.create({"id"=>65243, "question_id"=>69377, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Once a standard has been chosen, it is important that data collectors have the necessary tools to properly create or capture the metadata. </span><span style=\"font-weight: 400;\">Audits of collected metadata should occur at specific time intervals (e.g., bi-weekly) to ensure metadata is created properly and captured consistently throughout the project. <br /><br /></span><span style=\"font-weight: 400;\">Some tips for ensuring good metadata collection are:<br /></span>\r\n<ul>\r\n<li>Provide support documentation and routine metadata training to data collectors.</li>\r\n<li><span style=\"font-weight: 400;\">Provide data collectors with thorough data collection tools (e.g., field or lab sheets) so they are able to capture the necessary information.</span></li>\r\n<li><span style=\"font-weight: 400;\">Samples or notes recorded in a field or lab book should be scanned or photographed daily to prevent lost data.</span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"7488ee2f-b1c7-440e-8901-4cc5507377ae"})
Section.create({"id"=>22527, "title"=>"Storage and Backup", "description"=>nil, "number"=>5, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"581c58fe-5830-49e6-afcb-9560ae0b7a85"})
Question.create({"id"=>69378, "text"=>"What are the anticipated storage requirements for your project, in terms of storage space (in megabytes, gigabytes, terabytes, etc.) and the length of time you will be storing it?", "default_value"=>nil, "number"=>1, "section_id"=>22527, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"35e7a540-e037-4b95-9dcd-5e85638040c0"})
Annotation.create({"id"=>65244, "question_id"=>69378, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Storage-space estimates should take into account requirements for file versioning, backups and growth over time. </span><span style=\"font-weight: 400;\">A long-term storage plan is necessary if you intend to retain your data after the research project.</span>", "type"=>"guidance", "versionable_id"=>"3b5b4276-7470-4889-a001-bdd952db5933"})
Question.create({"id"=>69379, "text"=>"<p>How and where will your data be stored and backed up during your research project?</p>", "default_value"=>nil, "number"=>2, "section_id"=>22527, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"bd303b55-27c4-4f2c-9cb6-d91e96708b79"})
Annotation.create({"id"=>65245, "question_id"=>69379, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">The risk of losing data due to human error, natural disasters, or other mishaps can be mitigated by following the 3-2-1 backup rule: Have at least three copies of your data; store the copies on two different media; keep one backup copy offsite. </span><span style=\"font-weight: 400;\">Data may be stored using optical or magnetic media, which can be removable (e.g., DVD and USB drives), fixed (e.g., desktop or laptop hard drives), or networked (e.g., networked drives or cloud-based servers such as </span><a href=\"https://www.computecanada.ca/\"><span style=\"font-weight: 400;\">Compute Canada</span></a><span style=\"font-weight: 400;\">)</span><span style=\"font-weight: 400;\">. </span><span style=\"font-weight: 400;\">Each storage method has benefits and drawbacks that should be considered when determining the most appropriate solution.</span><br /><span style=\"font-weight: 400;\"><br />Raw data should be preserved and never altered. Some options for preserving raw data are storing on a read-only drive or archiving the raw, unprocessed data. The preservation of raw data should be included in the data collection process and backup procedures.<br /></span><br /><span style=\"font-weight: 400;\">Examples of further information on storage and backup practices are available from the</span> <a href=\"https://onesearch.library.utoronto.ca/researchdata/data-storage\"><span style=\"font-weight: 400;\">University of Toronto</span></a><span style=\"font-weight: 400;\">&nbsp;and the</span><a href=\"https://www.ukdataservice.ac.uk/manage-data/store.aspx\"> <span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span></p>", "type"=>"guidance", "versionable_id"=>"2efd47a8-a3fa-4800-9af8-7efa5bc3dafc"})
Question.create({"id"=>69380, "text"=>"How will the research team and other collaborators access, modify and contribute data throughout the project? How will data be shared?", "default_value"=>nil, "number"=>3, "section_id"=>22527, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"4d5c273d-bae6-4266-82ba-f0122a25f0ce"})
Annotation.create({"id"=>65246, "question_id"=>69380, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">An ideal shared data management solution facilitates collaboration, ensures data security and is easily adopted by users with minimal training. Tools such as the </span><a href=\"https://www.globus.org/\"><span style=\"font-weight: 400;\">Globus</span></a><span style=\"font-weight: 400;\"> file transfer system, currently in use by many academic institutions, allows data to be securely transmitted between researchers or to centralized project data storage. Relying on email for data transfer is not a robust or secure solution.&nbsp;<br /></span><br /><span style=\"font-weight: 400;\">Third-party commercial file sharing services (such as Google Drive and Dropbox) facilitate file exchange, but they are not necessarily permanent or secure, and are often located outside Canada. </span><span style=\"font-weight: 400;\">Additional resources such as </span><a href=\"https://osf.io/\"><span style=\"font-weight: 400;\">Open Science Framework</span></a><span style=\"font-weight: 400;\"> and </span><a href=\"https://www.computecanada.ca/\"><span style=\"font-weight: 400;\">Compute Canada</span></a><span style=\"font-weight: 400;\"> are also recommended options for collaborations.&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />If your data will be collected on Indigenous lands, how will you share data with community members throughout the project?&nbsp;</span><br /><span style=\"font-weight: 400;\"><br />Please contact librarians at your institution to determine if there is support available to develop the best solution for your research project.</span>", "type"=>"guidance", "versionable_id"=>"d833984c-8bb8-4bd9-ba0f-50d5da7799b5"})
Section.create({"id"=>22528, "title"=>"Responsibilities and Resources", "description"=>nil, "number"=>6, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"e7d0a07c-95ad-4f6a-97eb-0df9a0f798ab"})
Question.create({"id"=>69381, "text"=>"Who will be responsible for managing this project's data during and after the project, and for what major data management tasks will they be responsible?", "default_value"=>nil, "number"=>1, "section_id"=>22528, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"8955020a-c029-4de1-b1be-72ec94f8e5fa"})
Annotation.create({"id"=>65247, "question_id"=>69381, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Describe the roles and responsibilities of all parties with respect to the management of the data. Consider the following:</span><br />\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">If there are multiple investigators involved, what are the data management responsibilities of each investigator?&nbsp;</span></li>\r\n<li><span style=\"font-weight: 400;\">If data will be collected by students, clarify the student role versus the principal investigator role, and identify who will hold theIntellectual Property rights.&nbsp;&nbsp;</span></li>\r\n<li><span style=\"font-weight: 400;\">Will training be required to perform the data collection or data management tasks? If so, how will this training be administered and recorded?&nbsp;</span></li>\r\n<li><span style=\"font-weight: 400;\">Who will be the primary person responsible for ensuring compliance with the Data Management Plan during all stages of the data lifecycle?</span></li>\r\n<li><span style=\"font-weight: 400;\">Include the time frame associated with these staff responsibilities and any training needed to prepare staff for these duties. </span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"b9b42e92-5671-43ae-8552-ae98d6a04ac7"})
Question.create({"id"=>69382, "text"=>"How will responsibilities for managing data activities be handled if substantive changes happen in the personnel overseeing the project's data, including a change of Principal Investigator?", "default_value"=>nil, "number"=>2, "section_id"=>22528, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"fb4c9c46-7d0d-460c-aa56-660f5da91edd"})
Annotation.create({"id"=>65248, "question_id"=>69382, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Indicate a succession strategy for these data in the event that one or more people responsible for the data leaves (e.g., a student leaving after graduation). </span><span style=\"font-weight: 400;\">Describe the process to be followed in the event that the Principal Investigator leaves the project. In some instances, a co-investigator or the department or division overseeing this research will assume responsibility.</span>", "type"=>"guidance", "versionable_id"=>"241b9006-d901-4e4d-b128-ae9252d580d4"})
Question.create({"id"=>69383, "text"=>"What resources will you require to implement your data management plan? What do you estimate the overall cost for data management to be?", "default_value"=>nil, "number"=>3, "section_id"=>22528, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"a29447ae-d72c-4361-aef7-72cc4e75a5af"})
Annotation.create({"id"=>65249, "question_id"=>69383, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">This estimate should incorporate data management costs incurred during the project and those required for longer-term support for the data when the project is finished, such as the cost of preparing&nbsp; your data for deposit and repository fees. </span><span style=\"font-weight: 400;\">Some funding agencies state explicitly the support that they will provide to meet the cost of preparing data for deposit. This might include technical aspects of data management, training requirements, file storage &amp; backup and contributions of non-project staff. </span><span style=\"font-weight: 400;\">Can you leverage existing resources, such as Compute Canada Resources, University Library Data Services, etc. to support implementation of your DMP?<br /></span><br /><span style=\"font-weight: 400;\">To help assess costs, OpenAIRE&rsquo;s &lsquo;</span><a href=\"https://www.openaire.eu/how-to-comply-to-h2020-mandates-rdm-costs\"><span style=\"font-weight: 400;\">estimating costs RDM tool</span></a><span style=\"font-weight: 400;\">&rsquo; may be useful. </span>", "type"=>"guidance", "versionable_id"=>"95311d59-098e-40af-a45c-9f6ae938f0c1"})
Section.create({"id"=>22529, "title"=>"Sharing, Reuse and Preservation", "description"=>"<p><span style=\"font-weight: 400;\">In general, data collected using public funds should be preserved for future discovery and reuse. </span><span style=\"font-weight: 400;\">As you develop your data sharing strategy you will want to consider the following:</span></p>", "number"=>7, "phase_id"=>3506, "modifiable"=>false, "versionable_id"=>"63b58dbe-0928-409d-973f-e865180ca465"})
Question.create({"id"=>69384, "text"=>"Do you, your institution or collaborators have an existing data sharing strategy?", "default_value"=>nil, "number"=>1, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"0f48263a-dd45-4c97-a1ad-a5a341bc9a99"})
Annotation.create({"id"=>65250, "question_id"=>69384, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Use all or parts of existing strategies to meet your requirements.</span>", "type"=>"guidance", "versionable_id"=>"cff070f3-8808-4cfe-94b0-2cefc7118354"})
Question.create({"id"=>69385, "text"=>"Are there restrictions on sharing due to ethics or legal constraints?", "default_value"=>nil, "number"=>2, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"51d57e5a-f9a7-483d-854b-5e51c128d704"})
Annotation.create({"id"=>65251, "question_id"=>69385, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">In these instances, it is critical to assess whether data can or should be shared. It is necessary to comply with:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data treatment protocols established by the Research Ethics Board (REB) process, including data collection consent, privacy considerations, and potential expectations of data destruction;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data-sharing agreements or contracts.&nbsp; Data that you source or derive from a third party may only be shared in accordance with the original data sharing agreements or licenses;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Any relevant legislation.</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400;\"><strong>Note:</strong>&nbsp; </span>If raw or identifiable data cannot be shared, is it possible to share aggregated data, or to de-identify your data, or coarsen location information for sharing? If data cannot be shared, consider publishing descriptive metadata (data about the data), documentation and contact information that will allow others to discover your work.</p>", "type"=>"guidance", "versionable_id"=>"0555c6e1-8182-4a14-8312-4d2420f991c8"})
Question.create({"id"=>69386, "text"=>"What data will you be sharing and in what form? (e.g., raw, processed, analyzed, final).", "default_value"=>nil, "number"=>3, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"0a33b3d9-f0fe-4a9a-8448-622cc4737eb0"})
Annotation.create({"id"=>65252, "question_id"=>69386, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Think about what data needs to be shared to meet institutional or funding requirements, and what data may be restricted because of confidentiality, privacy, or intellectual property considerations.</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><strong>Raw data</strong><span style=\"font-weight: 400;\"> are unprocessed data directly obtained from sampling, field instruments, laboratory instruments,&nbsp; modelling,&nbsp; simulation, or survey.&nbsp; Sharing raw data is valuable because it enables researchers to evaluate new processing techniques and analyse disparate datasets in the same way.&nbsp;</span></li>\r\n<li style=\"font-weight: 400;\"><strong>Processed data</strong><span style=\"font-weight: 400;\"> results from some manipulation of the raw data in order to eliminate errors or outliers, to prepare the data for analysis or preservation, to derive new variables, or to de-identify the sampling locations. Processing steps need to be well described.</span></li>\r\n<li style=\"font-weight: 400;\"><strong>Analyzed data</strong><span style=\"font-weight: 400;\"> are the results of qualitative, statistical, or mathematical analysis of the processed data. They may&nbsp; be presented as graphs, charts or statistical tables.</span></li>\r\n<li style=\"font-weight: 400;\"><strong>Final data</strong><span> are processed data that have undergone a review process to ensure data quality and, if needed, have been converted into a preservation-friendly format.</span></li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"dd290184-dbe6-41fd-bba9-a94127e93d43"})
Question.create({"id"=>69387, "text"=>"Will you deposit your data for long-term preservation and access at the end of your research project? Please indicate any repositories that you will use.", "default_value"=>nil, "number"=>4, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"74ddaf0c-0a70-420e-b14c-734c1c857351"})
Annotation.create({"id"=>65253, "question_id"=>69387, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Data repositories help maintain scientific data over time and support data discovery, reuse, citation, and quality. Researchers are encouraged to deposit data in leading &ldquo;domain-specific&rdquo; repositories, especially those that are FAIR-aligned, whenever possible.<br /></span></span>\r\n<p><strong>Domain-specific repositories for water quality data include:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><a href=\"http://www.datastream.org\"><span style=\"font-weight: 400;\">DataStream</span></a><span style=\"font-weight: 400;\">: A free, open-access platform for storing, visualizing, and sharing water quality data in Canada.</span></li>\r\n<li style=\"font-weight: 400;\"><a href=\"https://www.canada.ca/en/environment-climate-change/services/canadian-aquatic-biomonitoring-network.html\"><span style=\"font-weight: 400;\">Canadian Aquatic Biomonitoring Network (CABIN)</span></a><span style=\"font-weight: 400;\">: A national biomonitoring program developed by Environment and Climate Change Canada that provides a standardized sampling protocol and a recommended assessment approach for assessing aquatic ecosystem condition.</span></li>\r\n</ul>\r\n<p><strong>General Repositories:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><a href=\"https://www.frdr-dfdr.ca/repo/\"><span style=\"font-weight: 400;\">Federated Research Data Repository</span></a><span style=\"font-weight: 400;\">: A scalable federated platform for digital research data management (RDM) and discovery.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Institution-specific Dataverse: Please contact your institution&rsquo;s library to see if this is a possibility.</span></li>\r\n</ul>\r\n<p><strong>Other resources:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">The </span><a href=\"https://repositoryfinder.datacite.org\"><span style=\"font-weight: 400;\">Repository Finder Tool</span></a><span style=\"font-weight: 400;\"> developed by DataCite. This tool queries the </span><a href=\"https://www.re3data.org/\"><span style=\"font-weight: 400;\">re3data registry</span></a><span style=\"font-weight: 400;\"> of research data repositories.</span></li>\r\n<li style=\"font-weight: 400;\">Coalition for Publishing Data in the Earth and Space Sciences: <a href=\"http://www.copdess.org/enabling-fair-data-project/enabling-fair-data-faqs/\">Enabling FAIR Data &ndash; FAQs</a>.</li>\r\n</ul>", "type"=>"guidance", "versionable_id"=>"bc6a8349-5047-4d54-89ca-e85cae503ab7"})
Question.create({"id"=>69388, "text"=>"What steps will you take to ensure your data is prepared for preservation?", "default_value"=>nil, "number"=>5, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"8a094adb-7390-4592-ac4c-f845aa2eb9dd"})
Annotation.create({"id"=>65254, "question_id"=>69388, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Consider using preservation-friendly file formats. For example, non-proprietary formats, such as text (.txt) and comma-separated (.csv), are considered preservation-friendly. For guidance, please see <a href=\"https://researchdata.library.ubc.ca/plan/format-your-data/\">UBC Library</a>, <a href=\"https://www.usgs.gov/products/data-and-tools/data-management/file-formats\">USGS</a>, <a href=\"https://www.dataone.org/best-practices/document-and-store-data-using-stable-file-formats\">DataONE</a>, or <a href=\"https://www.ukdataservice.ac.uk/manage-data/format.aspx\">UK Data Service</a></span><span style=\"font-weight: 400;\">. Keep in mind that files converted from one format to another may lose information (e.g., converting from an uncompressed TIFF file to a compressed JPG file degrades image quality), so changes to file formats should be documented.&nbsp;<br /><br /></span><span style=\"font-weight: 400;\">Some data you collect may be deemed sensitive and require unique preservation techniques, including anonymization. Be sure to note what this data is and how you will preserve it to ensure it is used appropriately. Read more about anonymization:</span> <a href=\"https://researchdata.library.ubc.ca/plan/protect-your-data/\"><span style=\"font-weight: 400;\">UBC Library</span></a> <span style=\"font-weight: 400;\">or</span> <a href=\"https://www.ukdataservice.ac.uk/manage-data/legal-ethical.aspx\"><span style=\"font-weight: 400;\">UK Data Service</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"d4d33b32-796e-46cc-a92a-f967009b44b2"})
Question.create({"id"=>69389, "text"=>"What type of end-user license will you use for your data?", "default_value"=>nil, "number"=>6, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"48ddc6c6-5c87-4252-9d8c-7cccbe1f6061"})
Annotation.create({"id"=>65255, "question_id"=>69389, "org_id"=>8, "text"=>"<span style=\"font-weight: 400;\">Licenses dictate how your data can be used. Funding agencies and data repositories may have end-user license requirements in place; if not, they may still be able to guide you in choosing or developing an appropriate license. Once determined, please consider including a copy of your end-user license with your Data Management Plan. Note that only the intellectual property rights holder(s) can issue a license, so it is crucial to clarify who owns those rights.&nbsp;<br /></span><br /><span style=\"font-weight: 400;\">There are several types of standard licenses available to researchers, such as the</span> <a href=\"https://creativecommons.org/choose/\"><span style=\"font-weight: 400;\">Creative Commons licenses</span></a><span style=\"font-weight: 400;\"> and the</span> <a href=\"https://opendatacommons.org/licenses/\"><span style=\"font-weight: 400;\">Open Data Commons licenses</span></a><span style=\"font-weight: 400;\">. For most datasets it is easier to use a standard license rather than to devise a custom-made one. Even if you choose to make your data part of the public domain, it is preferable to make this explicit by using a license such as Creative Commons' CC0. More about data licensing:</span> <a href=\"http://www.dcc.ac.uk/resources/how-guides/license-research-data\"><span style=\"font-weight: 400;\">UK DCC</span></a><span style=\"font-weight: 400;\">.</span>", "type"=>"guidance", "versionable_id"=>"43e9c1da-2f84-4d88-87c5-226b47a18071"})
Question.create({"id"=>69390, "text"=>"What steps will be taken to help the research community know that your data exists?", "default_value"=>nil, "number"=>7, "section_id"=>22529, "question_format_id"=>1, "option_comment_display"=>true, "modifiable"=>false, "versionable_id"=>"58474196-62d5-48c8-aa03-bd7eef908ba5"})
Annotation.create({"id"=>65256, "question_id"=>69390, "org_id"=>8, "text"=>"<p><span style=\"font-weight: 400;\">Possibilities include: data registries, repositories, indexes, word-of-mouth, publications. How will the data be accessed (Web service, ftp, etc.)? One of the best ways to refer other researchers to your deposited datasets is to cite them the same way you cite other types of publications. The Digital Curation Centre provides a detailed </span><a href=\"http://www.dcc.ac.uk/resources/how-guides/cite-datasets\"><span style=\"font-weight: 400;\">guide on data citation</span></a><span style=\"font-weight: 400;\">. Some repositories also create links from datasets to their associated papers, increasing the visibility of the publications. Read more at the National Institutes of Health&rsquo;s </span><a href=\"https://grants.nih.gov/grants/sharing_key_elements_data_sharing_plan.pdf\"><span style=\"font-weight: 400;\">Key Elements to Consider in Preparing a Data Sharing Plan Under NIH Extramural Support</span></a><span style=\"font-weight: 400;\">.</span></p>\r\n<span style=\"font-weight: 400;\">Contact librarians at your institution for assistance in making your dataset visible and easily accessible, or reach out to the Portage DMP Coordinator at </span><a href=\"mailto:support@portagenetwork.ca\"><span style=\"font-weight: 400;\">support@portagenetwork.ca</span></a><span style=\"font-weight: 400;\">.&nbsp; </span>", "type"=>"guidance", "versionable_id"=>"19ff75d4-cec0-4862-885c-4d0e7025584b"})
