nder_name":null},{"id":9609,"title":"Wild Winnipeg (University of Manitoba )","dmptemplate_id":35,"created_at":"2020-09-22 23:06:13 UTC","updated_at":"2020-09-22 23:10:36 UTC","slug":"wild-winnipeg-university-of-manitoba","organisation_id":37,"grant_number":"","identifier":"52529","description":"","principal_investigator":"Kamni Gill","principal_investigator_identifier":"","data_contact":"Mark Meagher, 204-396-1713, mark.meagher@umanitoba.ca","funder_name":null},{"id":9610,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-23 03:05:34 UTC","updated_at":"2020-09-23 03:05:34 UTC","slug":"my-plan-portage-template--3786","organisation_id":25,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Raisa Ladha","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9611,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-23 17:25:58 UTC","updated_at":"2020-09-23 17:25:58 UTC","slug":"my-plan-portage-template--3787","organisation_id":25,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Brie McConnell","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9612,"title":"Data Literacy Project","dmptemplate_id":12,"created_at":"2020-09-23 18:48:22 UTC","updated_at":"2021-02-18 17:49:50 UTC","slug":"data-literacy-project","organisation_id":8,"grant_number":"","identifier":"","description":"","principal_investigator":"Nora Mulvaney","principal_investigator_identifier":"","data_contact":"","funder_name":null},{"id":9614,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-24 09:57:15 UTC","updated_at":"2020-09-24 09:57:15 UTC","slug":"my-plan-portage-template--3790","organisation_id":25,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Xiaochun Yang","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9615,"title":"Troublesome Clients and Team Infighting: Improving Relationships in the Audit Workspace","dmptemplate_id":12,"created_at":"2020-09-24 13:14:29 UTC","updated_at":"2020-09-24 13:38:15 UTC","slug":"troublesome-clients-and-team-infighting-improving-relationships-in-the-audit-workspace","organisation_id":25,"grant_number":"","identifier":"","description":"The goals of the proposed research program are to extend the limited understanding (to date) of challenging or even harmful interactions and relationships that auditors or specialists experience and to create new knowledge about the ways in which these negative relationships can be ameliorated. \r\n\r\nIn particular the proposal will investigate, via interview and survey data, targets (auditors) and perpetrators (clients) of client-initiated aggression in the audit workplace by analyzing demographics of both parties (e.g., gender, race), as well as examine, via an experiment, the adverse effects of client aggression on auditors’ work quality and how effective coping mechanisms mitigate these adverse effects. The proposal will also investigate, via experiments, auditor infringement on non-audit specialists' workspace - how such infringement adversely affects specialists' work quality \r\nand what mechanisms have potential to effectively reduce these adverse effects.\r\n\r\nThe knowledge gained from this proposal will provide further insight into the uncomfortable interpersonal realities that can influence professionals and the work they perform within auditing, and shed light on potential interventions that could serve to diffuse the negative situations or protect the quality of work performed regardless. The resulting evidence will have important implications for accounting firms and their practicing auditors and specialists, as well as academics and regulators. Notably, this evidence will reveal the severity of problematic relationships in the audit workspace but also potential remedies to the problems.","principal_investigator":"Tim Bauer","principal_investigator_identifier":"http://orcid.org/0000-0002-9945-1711","data_contact":"tdbauer@uwaterloo.ca","funder_name":null},{"id":9616,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-24 14:25:17 UTC","updated_at":"2020-09-24 14:25:17 UTC","slug":"my-plan-portage-template--3792","organisation_id":27,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Mark Milne","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9617,"title":"Knowledge transfer on long term influence of Ontario biomass crops on soil carbon, health and GHG emissions in several grower fields","dmptemplate_id":45,"created_at":"2020-09-24 14:42:01 UTC","updated_at":"2020-09-24 15:39:25 UTC","slug":"knowledge-transfer-on-long-term-influence-of-ontario-biomass-crops-on-soil-carbon-health-and-ghg-emissions-in-several-grower-fields","organisation_id":22,"grant_number":"030515","identifier":"UG-KTTM-2020-100291","description":"Biomass crops could potentially increase soil organic carbon (SOC) sequestration, enhance soil health, water quality and support ecosystem services, which are Ontario Agri-Food Innovation Alliance\r\nresearch priorities. In the above context, Ontario Ministry of Agriculture, Food and Rural Affairs funded research projects have provided new information on SOC data linked to 19 biomass grower fields in Ontario, development of DayCent model to predict future SOC sequestration including\r\nvalidated results, the effects of biofertilizers on biomass crops and their influence on soil health, nutrients and greenhouse gas emissions reduction. The proposed KTT project will therefore disseminate new knowledge to biomass growers and grain crop producers through field-day events,\r\nworkshops, focus group discussion session, creation of pdf documents to be uploaded onto the Ontario Biomass Producer Cooperative website and updates to be included in the existing biomass\r\nagronomy manuals including information on new switchgrass cultivars produced by Resource Efficient Agricultural Production, Canada.","principal_investigator":"Naresh Thevathasan","principal_investigator_identifier":"https://orcid.org/0000-0001-7368-937X","data_contact":"Amir Bazrgar, abazrgar@uoguelph.ca","funder_name":null},{"id":9618,"title":"My plan (University of British Columbia Generic Template)","dmptemplate_id":11,"created_at":"2020-09-24 16:19:03 UTC","updated_at":"2020-09-24 16:20:26 UTC","slug":"my-plan-university-of-british-columbia-generic-template--587","organisation_id":7,"grant_number":"","identifier":"","description":"","principal_investigator":"","principal_investigator_identifier":"","data_contact":"Julie Fortin","funder_name":null},{"id":9619,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-24 17:19:40 UTC","updated_at":"2020-09-24 17:19:40 UTC","slug":"my-plan-portage-template--3793","organisation_id":22,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Christopher Popovich","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9620,"title":"Mon plan (Modèle Portage)","dmptemplate_id":12,"created_at":"2020-09-25 08:57:35 UTC","updated_at":"2020-09-25 08:57:35 UTC","slug":"mon-plan-modele-portage--557","organisation_id":29,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Danielle","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9621,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_at":"2020-09-25 17:30:21 UTC","updated_at":"2020-09-25 17:30:21 UTC","slug":"my-plan-portage-template--3794","organisation_id":10,"grant_number":null,"identifier":null,"description":null,"principal_investigator":"Neha Arora","principal_investigator_identifier":null,"data_contact":null,"funder_name":null},{"id":9622,"title":"My plan (Portage Template)","dmptemplate_id":12,"created_a